property.description:
use utf8; 
use Encode;
use ElectricCommander;

$| = 1;

my $str = "";

my @customMatchers = (
    { id => "gitCommitLog",
      pattern => q{(.*)},
      action => q{setLog($1);},
    },
);

push @::gMatchers,@customMatchers;

sub setLog($) {
   my $inputStr = decode('UTF-8', shift);
    $inputStr =~ s/^\s+|\s+$//g;  # 앞뒤에 공백을 제거
    $inputStr =~ s/\'/\\\'/g;

    if($inputStr eq ""){return;}

    if($inputStr =~ /Author: (.+) <((.+)@(.+))>/){
       setProperty("/myJob/commitUserName", $1);
       setProperty("/myJob/commitUserId", $2);
       setProperty("/myJob/commitEmail", $3);
    }
    if($inputStr =~ /HEAD -> ([a-zA-Z]+), tag: (([\d]|\.|[a-zA-Z]|[_!@#\$%*])+),/){
        setProperty("/myJob/GitTagName", $2);
    }
    if($inputStr =~ /HEAD, tag: (([\d]|\.|[a-zA-Z]|[_!@#\$%*])+)/){
        setProperty("/myJob/GitTagName", $1);
    }	

    $str = $str.$inputStr."\n";
   my $output = encode("cp949", $str );
    setProperty("/myJob/commitLog", $str);
};

property.value:
use utf8; 
use Encode;
use ElectricCommander;

$| = 1;

my $str = "";

my @customMatchers = (
    { id => "gitCommitLog",
      pattern => q{(.*)},
      action => q{setLog($1);},
    },
);

sub setLog($) {
   my $JIRA_PRJ_KEY = "APP";    # JIRA_PROJECT_KEY 입력
   setProperty("/myJob/JIRA_PRJKEY", "$JIRA_PRJ_KEY");

   my $inputStr = decode('UTF-8', shift);
    $inputStr =~ s/^\s+|\s+$//g;  # 앞뒤에 공백을 제거
    $inputStr =~ s/\'/\\\'/g;

    if($inputStr eq ""){return;}

    #     Author: YUNJU JUNG <yunju5572@plateer.com>
    if($inputStr =~ /Author: (.+) <((.+)@(.+))>/){
       setProperty("/myJob/commitUserName", $1);
       setProperty("/myJob/commitEmail", $2);
       setProperty("/myJob/commitUserId", $3);
    }

    #     APP-112 University.java edited online with Bitbucket
    if($inputStr =~ /($JIRA_PRJ_KEY-\d+)/){
       setProperty("/myJob/issueKey", $1);
    }


    if($inputStr =~ /HEAD -> ([a-zA-Z]+), tag: (([\d]|\.|[a-zA-Z]|[_!@#\$%*])+),/){
        setProperty("/myJob/GitTagName", $2);
    }
    if($inputStr =~ /HEAD, tag: (([\d]|\.|[a-zA-Z]|[_!@#\$%*])+)/){
        setProperty("/myJob/GitTagName", $1);
    }	

    $str = $str.$inputStr."<br>";
   my $output = encode("cp949", $str );
    setProperty("/myJob/commitLog", $str);
};

push @::gMatchers,@customMatchers;

sub addUserToGroup {
    my $ec = shift;
    my $groupName = shift; #user or group 
	my $userNames = shift;

	my $arrSize = @{$userNames};

	#Check User Information
	if($arrSize <= 0){
		print(" Error getting users information.\n");
        return 0;
	}

	foreach (@{$userNames}){
		my ($success, $res, $errMsg, $errCode) = invokeCommand($ec, "SuppressLog IgnoreError", "getUser", {userName=> "$_"});
		if (!$success) {
            print(" Error getting user information => UserName : $_\n");
            print(" $errCode: $errMsg\n");
            return 0;
        }
	}


	#Check Group Information 
    my ($success, $res, $errMsg, $errCode) = invokeCommand($ec, "SuppressLog IgnoreError", "getGroup", {groupName=> $groupName});
    if (!$success) {
        ($success, $res, $errMsg, $errCode) = invokeCommand($ec, "SuppressLog IgnoreError", "getGroup", {providerName=>"Mobis_ActiveDirectory", groupName=> $groupName});
        if (!$success) {
            print(" Error getting group information => GroupName : $groupName");
            print(" $errCode: $errMsg\n");
            return 0;
        } 
    }

	#AddUserToGroup
	($success, $res, $errMsg, $errCode) = invokeCommand($ec, "SuppressLog IgnoreError", "addUsersToGroup", $groupName, {userName=> $userNames});
    if (!$success) {
        print(" Error getting group information => GroupName : $groupName\n");
        print(" $errCode: $errMsg\n");
        return 0;
    } 	

	return 1;

}

sub changeGroupACE {
    my $ec = shift;
    my $groupName = shift; #user or group 
    my $readPrivilege = shift;
    my $modifyPrivilege = shift;
    my $executePrivilege = shift;
    my $changePermissionsPrivilege = shift;
    my $hasProvider = 0;

    my ($success, $res, $errMsg, $errCode) = 
            invokeCommand($ec, "SuppressLog IgnoreError", "getGroup", {groupName=> $groupName});


    if (!$success) {
        ($success, $res, $errMsg, $errCode) = 
            invokeCommand($ec, "SuppressLog IgnoreError", "getGroup", {providerName=>"Mobis_ActiveDirectory", groupName=> $groupName});

        if (!$success) {
            print(" Error getting group information => GroupName : $groupName");
            print(" $errCode: $errMsg\n");
            return 0;
        }

        $hasProvider = 1;
    }

    print "Changing group access information : GroupName - $groupName\n";

    ($success, $res, $errMsg, $errCode) = 
            invokeCommand($ec, "SuppressLog IgnoreError", "modifyAclEntry", 
                {principalType => "group", 
                 principalName => $groupName, 
                 groupName=> $groupName,
                 readPrivilege=>$readPrivilege, 
                 modifyPrivilege=>$modifyPrivilege, 
                 executePrivilege =>$executePrivilege,
                 changePermissionsPrivilege =>$changePermissionsPrivilege});

    if (! $success) {
        print(" Error setting access information => GroupName : $groupName");
        print(" $errCode: $errMsg\n");
        return 0;
    }

    printGroupAccessInfo($ec, $groupName);

    return 1;
}

sub changeUserACE {
    my $ec = shift;
	my $userName = shift; #user or group 
	my $readPrivilege = shift;
	my $modifyPrivilege = shift;
	my $executePrivilege = shift;
	my $changePermissionsPrivilege = shift;
	my $hasProvider = 0;

 	my ($success, $res, $errMsg, $errCode) = 
			invokeCommand($ec, "SuppressLog IgnoreError", "getUser", {userName=> $userName});


	if (!$success) {
		($success, $res, $errMsg, $errCode) =  
			invokeCommand($ec, "SuppressLog IgnoreError", "getUser", {providerName=>"Mobis_ActiveDirectory", userName=> $userName});

		if (!$success) {
			print("  Error getting user information => UserName : $userName");
			print("  $errCode: $errMsg\n");
			return 0;
		}

		$hasProvider = 1;
	}

	print "Changing user access information : UserName - $userName\n";

	($success, $res, $errMsg, $errCode) =  
			invokeCommand($ec, "SuppressLog IgnoreError", "modifyAclEntry", 
				{principalType => "user", 
				 principalName => $userName, 
				 userName=> $userName,
				 readPrivilege=>$readPrivilege, 
				 modifyPrivilege=>$modifyPrivilege, 
				 executePrivilege =>$executePrivilege,
				 changePermissionsPrivilege =>$changePermissionsPrivilege});

	if (! $success) {
		print("  Error setting access information => UserName : $userName");
		print("  $errCode: $errMsg\n");
		return 0;
	}

	printUserAccessInfo($ec, $userName);

	return 1;
}

sub printGroupAccessInfo {
    my $ec = shift;
    my $groupName = shift;

    my ($success, $res, $errMsg, $errCode)= invokeCommand($ec, "SuppressLog IgnoreError", "getAccess", { 'groupName' => $groupName}); 
    if (! $success) {
        print("  Error getting group access information => GroupName : $groupName");
        print(" $errCode: $errMsg\n");
        return 0;
    }

    #print $res ->findnodes_as_string('/') . "\n";


    my $nodeset = $res->findnodes("//object");    

    foreach ($nodeset->get_nodelist) {
        if($_->findvalue("objectType") ne 'group') {
            next;
        }

        my $aclNodeset = $_->findnodes("aclEntry");
        foreach my $elem ($aclNodeset->get_nodelist) {
            print' readPrivilege : '. $elem->findvalue("readPrivilege")."\n";
            print' modifyPrivilege : '. $elem->findvalue("modifyPrivilege")."\n";
            print' executePrivilege : '. $elem->findvalue("executePrivilege")."\n";
            print' changePermissionsPrivilege : '. $elem->findvalue("changePermissionsPrivilege")."\n";
        }
    }

    return 1;
}

