property.description:
sub checkEntireAgentStatus {
	my $total = 0;
	my $dead = 0;
	my $live = 0;
	my $disabled = 0;
	my $nohb = 0;
	my $wating = 0;

	my $strp = DateTime::Format::Strptime->new(on_error=>'croak',pattern => '%Y-%m-%dT%H:%M:%S',time_zone => 'UTC',);

	my $agentList = `/opt/ecloud/i686_Linux/bin/cmtool --server=10.230.110.154 getAgents`;
	my $parser = XML::LibXML->new(); 
	my $res = $parser->parse_string($agentList); 

	if ("1" ne $res->findvalue('//response/@requestId')) {
		print "EA_AGENTS UNKNOWN  Failed to connect to server\n";
		return 0;	
	}

	foreach my $agent ($res->findnodes('//agent')) { 
		$total = $total + 1;

		my $enabled = $agent->findvalue('./enabled');
		if ($enabled ne "1") {
			$disabled = $disabled + 1;
			next;
		}

		my $status = $agent->findvalue('./status');
		if ($status ne "1") {
			$dead = $dead + 1;
			next;
		}

		$live = $live + 1;

		my $lastPingTime = $agent->findvalue('./lastPingTime');
		$lastPingTime =~ tr/Z//;

		my $dt = $strp->parse_datetime($lastPingTime); 
		my $lptime = $dt->epoch;

		my $ctime = time() - 300;
		if ($lptime < $ctime) {
			$nohb = $nohb + 1;
			next;
		}  

		my $statusDetail = $agent->findvalue('./statusDetail');
		if( beginWith($statusDetail,'Waiting for emake to connect') || beginWith($statusDetail,'OK') ) {
			$wating = $wating + 1;
		} else {
			print $statusDetail."\n";
		}
	}

	if ((0 < $dead) or (0 < $nohb)) {
		print "EA_AGENTS CRITICAL | TOTAL=$total DISABLED=$disabled LIVE=$live DEAD=$dead NOHEARTBEAT=$nohb WAITING=$wating\n";	
	} else {
		print "EA_AGENTS OK | TOTAL=$total DISABLED=$disabled LIVE=$live DEAD=$dead NOHEARTBEAT=$nohb WAITING=$wating\n";
	}

	return ($total, $disabled, $live, $dead, $nohb,$wating);
}

property.value:
sub checkEntireAgentStatus {
	my $total = 0;
	my $dead = 0;
	my $live = 0;
	my $disabled = 0;
	my $nohb = 0;
	my $wating = 0;
	my $cm = '$[/projects/CO_CONF_EA/EA/EA_CM_ADDRESS]';

	my $strp = DateTime::Format::Strptime->new(on_error=>'croak',pattern => '%Y-%m-%dT%H:%M:%S',time_zone => 'UTC',);

	my $agentList = `/opt/ecloud/i686_Linux/bin/cmtool --server=$cm getAgents`;
	my $parser = XML::LibXML->new(); 
	my $res = $parser->parse_string($agentList); 

	if ("1" ne $res->findvalue('//response/@requestId')) {
		print "EA_AGENTS UNKNOWN  Failed to connect to server\n";
		return 0;	
	}

	foreach my $agent ($res->findnodes('//agent')) { 
		$total = $total + 1;

		my $enabled = $agent->findvalue('./enabled');
		if ($enabled ne "1") {
			$disabled = $disabled + 1;
			next;
		}

		my $status = $agent->findvalue('./status');
		if ($status ne "1") {
			$dead = $dead + 1;
			next;
		}

		$live = $live + 1;

		my $lastPingTime = $agent->findvalue('./lastPingTime');
		$lastPingTime =~ tr/Z//;

		my $dt = $strp->parse_datetime($lastPingTime); 
		my $lptime = $dt->epoch;

		my $ctime = time() - 300;
		if ($lptime < $ctime) {
			$nohb = $nohb + 1;
			next;
		}  

		my $statusDetail = $agent->findvalue('./statusDetail');
		if( beginWith($statusDetail,'Waiting for emake to connect') || beginWith($statusDetail,'OK') ) {
			$wating = $wating + 1;
		} else {
			print $statusDetail."\n";
		}
	}

	if ((0 < $dead) or (0 < $nohb)) {
		print "EA_AGENTS CRITICAL | TOTAL=$total DISABLED=$disabled LIVE=$live DEAD=$dead NOHEARTBEAT=$nohb WAITING=$wating\n";	
	} else {
		print "EA_AGENTS OK | TOTAL=$total DISABLED=$disabled LIVE=$live DEAD=$dead NOHEARTBEAT=$nohb WAITING=$wating\n";
	}

	return ($total, $disabled, $live, $dead, $nohb,$wating);
}

sub checkEntireAgentStatusFromHosts {
    my $hosts = shift;

    my $total = 0;
    my $dead = 0;
    my $live = 0;
    my $disabled = 0;
    my $nohb = 0;
    my $wating = 0;
	my $using = 0;
    my $watingFromHost = 0;
    my $cm = '$[/projects/CO_CONF_EA/EA/EA_CM_ADDRESS]';

    my $strp = DateTime::Format::Strptime->new(on_error=>'croak',pattern => '%Y-%m-%dT%H:%M:%S',time_zone => 'UTC',);

    my $agentList = `/opt/ecloud/i686_Linux/bin/cmtool --server=$cm getAgents`;
    my $parser = XML::LibXML->new(); 
    my $res = $parser->parse_string($agentList); 

    if ("1" ne $res->findvalue('//response/@requestId')) {
        print "EA_AGENTS UNKNOWN Failed to connect to server\n";
        return 0;    
    }

    foreach my $agent ($res->findnodes('//agent')) { 
        $total = $total + 1;

        my $enabled = $agent->findvalue('./enabled');
        if ($enabled ne "1") {
            $disabled = $disabled + 1;
            next;
        }

        my $status = $agent->findvalue('./status');
        if ($status ne "1") {
            $dead = $dead + 1;
            next;
        }

        $live = $live + 1;

        my $lastPingTime = $agent->findvalue('./lastPingTime');
        $lastPingTime =~ tr/Z//;

        my $dt = $strp->parse_datetime($lastPingTime); 
        my $lptime = $dt->epoch;

        my $ctime = time() - 300;
        if ($lptime < $ctime) {
            $nohb = $nohb + 1;
            next;
        } 

        my $statusDetail = $agent->findvalue('./statusDetail');
        if( beginWith($statusDetail,'Waiting for emake to connect') || beginWith($statusDetail,'OK') ) {
            $wating = $wating + 1;

            my $hostName = $agent->findvalue('./hostName');
            my @hostNames = split /-/, $hostName, 2;
            if(grep( /^$hostNames[0]$/, @{$hosts})) {
                $watingFromHost = $watingFromHost + 1;
            }

        } else {
            my $hostName = $agent->findvalue('./hostName');
            print "HOST: $hostName, STATUS: $statusDetail \n";
			$using = $using + 1;
        }
    }

    if ((0 < $dead) or (0 < $nohb)) {
        print "EA_AGENTS CRITICAL | TOTAL=$total DISABLED=$disabled LIVE=$live DEAD=$dead NOHEARTBEAT=$nohb WAITING=$wating USING=$using WAITING_FROM_HOSTS=$watingFromHost\n";    
    } else {
        print "EA_AGENTS OK | TOTAL=$total DISABLED=$disabled LIVE=$live DEAD=$dead NOHEARTBEAT=$nohb WAITING=$wating USING=$using WAITING_FROM_HOSTS=$watingFromHost\n";
    }

    return ($total, $disabled, $live, $dead, $nohb,$wating, $using, $watingFromHost);
}

sub deleteOldBuildsByDate {
    my $daysBefore = shift; 

    my $response = `/opt/ecloud/i686_Linux/bin/cmtool deleteBuilds --filter "start_time <date_sub(curdate(), interval $daysBefore day)"`;
    my $parser = XML::LibXML->new(); 
    my $res = $parser->parse_string($response); 

    if ("1" ne $res->findvalue('//response/@requestId')) {
        print "FAILED TO DELETE BUILDS\n";
        return 0;    
    }

    return 1;
}

sub getBuildClassResourceName {
	my $targetBuildClassName = shift; 
	my $cm = '$[/projects/CO_CONF_EA/EA/EA_CM_ADDRESS]';

	my $buildClassList = `/opt/ecloud/i686_Linux/bin/cmtool --server=$cm getBuildClasses`;
	my $parser = XML::LibXML->new(); 
	my $res = $parser->parse_string($buildClassList); 

	if ("1" ne $res->findvalue('//response/@requestId')) {
		print "EA_BUILD_CLASSES UNKNOWN  Failed to connect to server\n";
		return 0;	
	}

	foreach my $buildClass ($res->findnodes('//buildClass')) { 

		my $buildClassName = $buildClass->findvalue('./buildClassName');
		if ("$targetBuildClassName" ne "$buildClassName") {
			next;
		}

		my $resName = $buildClass->findvalue('./resourceRequest');

		return "$resName";
	}

	return "";
}

property.description:
sub getBuildDuration  {
	my $jobName = shift;
	my $cm = '$[/projects/CO_CONF_EA/EA/EA_CM_ADDRESS]';
    my $response = `/opt/ecloud/i686_Linux/bin/cmtool --server=$cm --output=simple --fields="buildId,buildName,startTime,duration" getBuilds --filter="buildName LIKE '$jobName%'"`;

	print "---Found Build Results---\n";
	print "$response\n";

	$response =~ s/^\s+|\s+$//g;
    my @items = grep { $_ ne '' } split(/\n/, $response);

	shift @items;

	#149114 AVN_PREMIUM6_CPU_AVN_PREMIUM6_DAILYBUILD_1305485_20181205_223000-opkg-utils-native 2018-12-05T13:39:58.000Z 396

	my $sum = 0;
	foreach my $var (@items) { 

		my @item = split / /, $var;
		my $startTime = $item[-2];
		my $buildDuration = $item[-1];

		my $start = str2time($startTime);
		my $end = $start + $buildDuration;

		print "START : $start , END : $end";

		$sum += $buildDuration;
	}  

    my $seconds = ($sum/1000)%60;
    my $minutes = ($sum/(1000*60))%60;
    my $hours = ($sum/(1000*60*60))%24;

	print "---Total Build Durations---\n";
	print "$hours:$minutes:$seconds";

	return $sum;

}

property.value:
sub checkTime {

	my $arf = shift;
	my $arf1 = shift; 

	if(!defined $arf) {

	}

	my $s = $arf->[0];
	my $e = $arf->[1];
	my $s1 = $arf1->[0];
	my $e1 = $arf1->[1];



	# s----------e
	#   s1----e1	

	# s----------e
	#     s1----e1	

	#  s----------e
	# s1----e1	

	#   s----e
	#  s1----e1	

	#s----------e
	#  s1----e1	
	if ( $s<=$s1 && $e1<=$e) {
		return 0;
	}

	#s----e
	#  s1----e1

	#s----e
	#     s1----e1
	if($s1>$s && $s1<= $e &&  $e < $e1) {
		return 1;
	}

 	#s----e
	#          s1----e1
	if($s1>$e) {
		return 2;
	}

	# s----------e
	#s1--------------e1	

	#   s----------e
	#s1--------------e1	

	#     s----------e
	#s1--------------e1	
	if ( $s1<=$s && $e<=$e1) {
		return 3;
	}

	return return 4;
}



sub getBuildDuration  {
	my $jobName = shift;
	my $cm = '$[/projects/CO_CONF_EA/EA/EA_CM_ADDRESS]';
    my $response = `/opt/ecloud/i686_Linux/bin/cmtool --server=$cm --output=simple --fields="buildId,buildName,startTime,duration" getBuilds --filter="buildName LIKE '$jobName%'"`;

	#149114 AVN_PREMIUM6_CPU_AVN_PREMIUM6_DAILYBUILD_1305485_20181205_223000-opkg-utils-native 2018-12-05T13:39:58.000Z 396
	$response =~ s/^\s+|\s+$//g;
    my @items = grep { $_ ne '' } split(/\n/, $response);
	shift @items;

	my $sizeOfItems = scalar @items;
	if( $sizeOfItems == 0 ){
		return "emake compile times\n00:00:00";
	}

	my @itemTimes = (); 
	my $sum = 0;
	foreach my $var (@items) { 

		my @item = split / /, $var;
		my $buildName = $item[-3];
		my $startTime = $item[-2];
		my $buildDuration = $item[-1];

		my $start = str2time($startTime)*1000 + 0;
		my $end = $start + $buildDuration + 0;
		my $endTime = time2str(int($end/1000));
		$startTime =  time2str(int($start/1000));

		print "Build : $buildName ($startTime ~ $endTime) \n";

		my @itemTime = ($start, $end);
		push @itemTimes, \@itemTime;

		$sum += $buildDuration;
	}  

	#Sorting Result
	#
	my @itemTimeSort = sort { $a->[0] <=> $b->[0] } @itemTimes;
		#print "\nSorted Array : ".Dumper(\@itemTimeSort)."\n";

	#Calculating Build Time Considering Parallel Builds
	#
	my @resultArr = ();
	while(scalar(@itemTimeSort) > 1) {
		my $item1 = shift @itemTimeSort;
		my $item2 = shift @itemTimeSort;

		my $ret = checkTime($item1,$item2);
		if($ret == 0) {
			unshift @itemTimeSort, $item1;
				#print "포함됨 : ($item1->[0], $item1->[1]) ($item2->[0], $item2->[1])\n";
		}elsif ($ret == 1 ) {
			my $s = $item1->[0];
			my $e = $item2->[1];
			my @newItem = ($s,$e);
			unshift @itemTimeSort, \@newItem;
				#print "교차함 : ($item1->[0], $item1->[1]) ($item2->[0], $item2->[1])\n";
		}elsif ($ret == 2 ) {
			push @resultArr,$item1;
			unshift @itemTimeSort, $item2;
				#print "만나지 않음 : ($item1->[0], $item1->[1]) ($item2->[0], $item2->[1])\n";
		}elsif ($ret == 3 ) {
			unshift @itemTimeSort, $item2;
				#print "포함됨 : ($item1->[0], $item1->[1]) ($item2->[0], $item2->[1])\n";
		}else{
			print "데이터 분석불가\n";
			my $s = $item1->[0];
			my $e = $item1->[1];
			my $s1 = $item2->[0];
			my $e1 = $item2->[1];
			print "($s,$e), ($s1,$e1)\n";
			exit 1;
		}
	}
	push @resultArr, @itemTimeSort;
		#print "\nResult Array : ".Dumper(\@resultArr)."\n";

	#Print Result
	#
    my $seconds = ($sum/1000)%60;
    my $minutes = ($sum/(1000*60))%60;
    my $hours = ($sum/(1000*60*60))%24;

	print "\n---Total Build Durations---\n\n";
	print "$hours:$minutes:$seconds\n";

	my $mergeSum = 0;
	foreach my $var (@resultArr) { 
		my $s = $var->[0];
		my $e = $var->[1];	
		$mergeSum = int($mergeSum) +  int($e) - int($s);
	}
    $seconds = ($mergeSum/1000)%60;
    $minutes = ($mergeSum/(1000*60))%60;
    $hours = ($mergeSum/(1000*60*60))%24;

	print "\n---Real Build Durations---\n\n";
	print "$hours:$minutes:$seconds\n";

	return "emake compile times\n$hours:$minutes:$seconds";
}

