sub getResoucesHostMasks {
	my $targetResName = shift; 
	my $cm = '$[/projects/CO_CONF_EA/EA/EA_CM_ADDRESS]';

	my $resourceList = `/opt/ecloud/i686_Linux/bin/cmtool --server=$cm getResources`;
	my $parser = XML::LibXML->new(); 
	my $res = $parser->parse_string($resourceList); 

	if ("1" ne $res->findvalue('//response/@requestId')) {
		print "EA_RESOURCES UNKNOWN  Failed to connect to server\n";
		return 0;	
	}

	foreach my $resource ($res->findnodes('//resource')) { 

		my $resourceName = $resource->findvalue('./resourceName');
		if ("$targetResName" ne "$resourceName") { 
			next;
		} 

		my $hostMasks = $resource->findvalue('./hostMasks');

		$hostMasks =~ s/[#\-%&\$*+()]//g;
		my @hostPrefix = split /\;/, $hostMasks;
		return \@hostPrefix;
	} 

	return undef;
}

