sub CHECK_EF_FUNC_ERROR {
    my ($ec, $action, @params) = @_;

    my $functionName = $action;

    $action = \&$action;

	my $res = undef;
	if(defined \@params) {
		$res = $action->($ec, @params);
	} else {
		$res = $action->($ec);
	} 

    my $hasError = 0;

    unless (defined $res) {
        print "The $functionName returned NULL\n";
        $hasError = 1;

    } else {
        if ($res =~ /^\d+?$/) {
            unless($res) {
                print "The $functionName returned FALSE\n";
                $hasError = 1; 
            }
        } else {
         my $reftype = ref $res;

            if ( !defined $reftype ) {
                print "The $functionName returned UNDEF\n";
                $hasError = 1;
            } elsif ( $reftype eq 'HASH' ) {

                my $size = keys %{$res};
                unless ($size > 0) {
                    print "The $functionName returned empty HASH\n";
                    $hasError = 1;
                }
            } elsif ( $reftype eq 'ARRAY' ) {
                my $size = @{$res};
                unless ($size > 0) {
                    print "The $functionName returned empty ARRAY\n";
                    $hasError = 1;
                }
            } elsif ( $reftype eq 'SCALAR' ) {
                unless($res) {
                    print "The $functionName returned FALSE\n";
                    $hasError = 1; 
                }
            } elsif ( $reftype eq 'XML::XPath::NodeSet') {
            } else {
            }
        }
    }

    if($hasError) {
		if(defined \@params) {
			foreach(@params) {
				my $param = $_;
				print  "\tParam : $param\n";

				my $reftype = ref $param;
				if ( $reftype eq 'HASH' ) {
					foreach ( sort keys %{$param} ) {
						print "\t\t$_ => $param->{$_}\n"
					}
					next;
				} 

				if ( $reftype eq 'ARRAY' ) {
					foreach ( @{$param} ) {
						print "\t\t$_\n"
					}
					next;
				}
			}
		} 

        setResultAndError($ec, "","$[/myProject/errorCode/funcExecuteError]-$functionName");
    }

    return $res;
}

sub getWorkingDirectory {
    my $path = getcwd(); 

    $path =~ s/\\/\//g;
    $path =~ s/\/\//\//g;

    return $path;
}

sub invokeCommand {
    my $ec = shift;
    my $optionFlags = shift;
    my $commanderFunction = shift;
    my $xPath;
    my $success = 1;

    my $bSuppressLog = $optionFlags =~ /SuppressLog/i;
    my $bSuppressResult = $bSuppressLog || $optionFlags =~ /SuppressResult/i;
    my $bIgnoreError = $optionFlags =~ /IgnoreError/i;


    $ec->abortOnError(0) if $bIgnoreError;
    $xPath = $ec->$commanderFunction(@_);
    $ec->abortOnError(1) if $bIgnoreError;

	unless(defined $xPath){
		$ec->abortOnError(1);
		$xPath = $ec->$commanderFunction(@_);
		unless(defined $xPath){
			$success = 0;
			my $errMsg = "Request could not be processed in ElectricFlow";
			my $errCode = "";
			return ($success, $xPath, $errMsg, $errCode);
		}
	}

    # Check for error return
    my $errMsg = $ec->checkAllErrors($xPath);
    my $errCode=$xPath->findvalue('//code')->value();
    if ($errMsg ne "") {
        $success = 0;
    }
    if ($xPath) {
        print "Return data from Commander:\n" .
               $xPath->findnodes_as_string("/") . "\n"
            unless $bSuppressResult;
    }

    # Return the result
    return ($success, $xPath, $errMsg, $errCode);
}

sub lock {
    my $ec = shift;
    my $xPath = $ec->getProperty("/myProject/scripts/mutexProperty");
    my $mutex = $xPath->findvalue("//value")->string_value();

    while($mutex >= 1) {
        $ec->setProperty("summary", "Waiting until finish another job..");
        sleep(5);

        $xPath = $ec->getProperty("/myProject/scripts/mutexProperty");
        $mutex = $xPath->findvalue("//value")->string_value();
    }

    $xPath = $ec->incrementProperty("/myProject/scripts/mutexProperty",1);

    $ec->setProperty("summary", "Mutex acquired");
}

sub setResultAndError {
    my $ec = shift;
    my $result = shift;
    my $errorMsg = shift;

    $ec->setProperty("/myJob/multiResult","$result");
    $ec->setProperty("/myJob/multiError","$errorMsg");

    print "\n";
    print "################################################################\n";
    print "\n";

    if($errorMsg ne '') {
        $ec->setProperty("summary", $errorMsg);
        print "Error : $errorMsg \n";
        print "\n";
        exit 1;
    } else {
        print "Result : $result\n";
        print "\n";
    }
}

sub setSummary {
	my $ec = shift;
	my $msg = shift; 
	$ec->setProperty("summary", $msg);   
}

sub loginByCred {
	my $ec = shift;
	my $credName = shift;

	my $user=$ec->getFullCredential($credName, {value => "userName"})->findvalue("//userName"); 
	my $pwd =$ec->getFullCredential($credName, {value => "password"})->findvalue("//password");
	$ec->login($user, $pwd); 
}

package RuleSelector;

$[/myProject/scripts/expressionFuncs/solveBooleanExpression]

sub new {
    my $class = shift;

    my $rules = shift;
    my $denyString = shift;

    my $self;

    $self->{_HAS_CONFLICT} = 0;
    $self->{_COUNT} = 0;
    $self->{_COND} = {};
    $self->{_RULES} = {};

    return bless $self, $class;
}

sub setConflict {
    my $self = shift;  
    $self->{_HAS_CONFLICT} = 1;
}

sub resetConflict {
    my $self = shift;  
    $self->{_HAS_CONFLICT} = 0;
}

sub hasConflict {
    my $self = shift; 
    return $self->{_HAS_CONFLICT};
}

sub clearConditions {
    my $self = shift;

    $self->{_COND} = {};
}

sub clearRules {
    my $self = shift;

    $self->{_RULES} = {};
	$self->{_COUNT} = 0;
	$self->{_HAS_CONFLICT} = 0;
}

sub fetch {
    my $self = shift;
    my $rule = shift;

    foreach ( sort keys %{$rule} ) {
        unless(exists $self->{_COND}{$_}) {
            return 0;
        }
    }
    return 1;
}

sub addKey {
    my $self = shift;
    my $key = shift; 

    $self->{_COND}{$key} = 1;
}

 sub addKeys {
    my $self = shift;
    my $keys = shift; 

    foreach ( @{ $keys } ) {
         $self->{_COND}{$_} = 1;
    }
}

sub addRule {
    my $self = shift;
    my $rule = shift; 

    $self->{_RULES}{$self->{_COUNT}} = $rule;
    $self->{_COUNT} = $self->{_COUNT} + 1;
}

 sub addRules {
    my $self = shift;
    my $rules = shift; 

    foreach ( @{ $rules } ) {
        $self->addRule($_);
    }
}

sub checkRuleBoundary {
    my $self = shift;
    my $res = 1;

    my @invalidRule = ();
    foreach ( sort keys %{$self->{_RULES}} ) {
        unless($self->fetch($self->{_RULES}{$_})) { 
            push(@invalidRule,$self->{_RULES}{$_});
        }
    }

    my $invalidRuleSize = @invalidRule;

    if($invalidRuleSize > 0) {
        print "\tRule Boundary Exception Occur: \n\n";
        print "\t  Allow Option : \n\t\t";

        foreach ( sort keys %{$self->{_COND}} ) {
            print "{$_} ";
        }

		print "\n\n";

        print "\t  Boundary Exception Rule : \n";        
        foreach ( @invalidRule ) { 
            printRule($_);     
        }

		$res = 0;
    }

    return $res;
}

sub printRule {
    my $href = shift;

	foreach ( sort keys %{$href} ) {
		my $key = $_;
        my $value = $href->{$key}{Value};
        my $propertyValue = $href->{$key}{PropertyValue}; 
        my $ruleOperation = $href->{$key}{Operation};
		my $andOrOperation = $href->{$key}{AndOrOperation};
        print "\t\t$key => PropertyValue : $propertyValue , Operation : $ruleOperation , Value : $value, EndOrOperation : $andOrOperation\n";
    }

    print "\n";
}

sub searchAppropriateRuleAmongVariousRules{
    my $self = shift;

	$self->resetConflict();

    print "Default's various rules checking ... \n\n";

    unless($self->checkRuleBoundary()) {
        return undef;
    }

	my @rules = ();
    my @succeedRules = ();
    my @succeedRuleIndexs = ();

    foreach ( sort keys %{$self->{_RULES}} ) {
		my $input = "";
        print "\tRule[$_]{\n";

        my $rule = $self->{_RULES}{$_};
        my $ruleIndex = $_;

        printRule($rule);

		my $checkPossibleParse = 1;
        foreach ( sort keys %{$rule} ) {
            my $ruleKey = $_;
            my $rulePropertyValue = $rule->{$_}{PropertyValue};
            my $ruleValue = $rule->{$_}{Value};
            my $ruleOperation = $rule->{$_}{Operation};
			my $andOrOperation = $rule->{$_}{AndOrOperation};

			if(defined $rulePropertyValue && $rulePropertyValue eq "" && 
			   defined $ruleValue && $ruleValue eq "") {
				$rulePropertyValue = "NO_DATA_WARNING";
				$ruleValue = "NO_DATA_WARNING";
			} else {
				unless(defined $rulePropertyValue && $rulePropertyValue ne "") {
					$checkPossibleParse = 0;
				}
				unless(defined $ruleValue && $ruleValue ne "") {
					$checkPossibleParse = 0;
				}			
			}

            $input = $input. "(\"$rulePropertyValue\"$ruleOperation\"$ruleValue\")$andOrOperation"
        }

        if((substr($input, -2) eq '&&') || (substr($input, -2) eq '||')){
            $input = substr($input, 0, length($input)-2);
        }

        print "\t\t[EXPRESSION] : ".$input."\n";

		my $result = 0;
		if($checkPossibleParse) {
			tokenize($input); 

			my $parse_tree = expression();
			$result = execute($parse_tree);
			if($result == 1) {
				push(@succeedRules,$rule);
				push(@succeedRuleIndexs, $ruleIndex);
			}
		}else{

		}

        push(@rules, $rule);

        print "\t\t[RESULT] : ".$result."\n";
        print "\t}\n\n";
    }

    my $i = 0;
    my $j = 0;
    my $succeedRuleSize = @succeedRules;

    unless($succeedRuleSize) {
        return undef;
    }


	print "\tChecking conflictions ...\n\n";

    my $maxSize = -1; 
    foreach (@succeedRuleIndexs) { 
        my $rule = $rules[$_];
		my $ruleIndex = $_;
        my $size = keys %{$rule};
        if($maxSize < $size) {
            $maxSize = $size; 
        }
    }

	if($maxSize <= 0 ) {
        return undef;
    }


	my $targetRule = undef;
    my $targetRuleIndex = -1;
	while($maxSize > 0) { 
		my @conflictionRuleIndex = ();

		foreach (@succeedRuleIndexs) { 
			my $rule = $rules[$_];
			my $ruleIndex = $_;
			my $ruleCount = keys %{$rule};
			if($ruleCount == $maxSize) { 
				push(@conflictionRuleIndex, $ruleIndex);
			}
		}

		my $ruleCount = @conflictionRuleIndex;
		if($ruleCount > 1) {
			print "\t\tRULE CONFLICTION OCCUR - {\n";
			foreach(@conflictionRuleIndex) {
				print "\t\t\tRule[$conflictionRuleIndex[$_]]\n";
			}
			print "\t\t} \n";

			$self->setConflict();

			return undef;
		} else {
			$targetRuleIndex = $conflictionRuleIndex[0];
			$targetRule = $rules[$targetRuleIndex];
			print "\t\tThere is no confliction between passing rules.\n\n";
			last;
		}

		$maxSize--; 
	}

	print "\tFinding target rule between passing rules...\n\n";    
    unless($targetRule) {
		print "\t\tFail to find target rule..\n";
        return undef;
    }

    print "\t\tTarget => Rule[$targetRuleIndex]{\n";
	printRule($targetRule);
    print "\t\t}\n\n";

	return $targetRuleIndex;
}

1;

sub checkDefaultOptionList {
	my $ec = shift;
	my $moduleName = shift;

	my $optionListStrs = getPropertyStringValue($ec, "/myProject/mdList/$moduleName/OptionList");
	if (defined $optionListStrs && $optionListStrs ne "") { 
		my $optionLabels = CHECK_EF_FUNC_ERROR($ec,"getPropertiesRefRecursively", "/myProject/procedures/CO_Set_Options/optionLabels");
		my @optionLists = split /\|/, $optionListStrs;

		my $invalidOptionName = '';
		foreach ( @optionLists ){
			my $optionName = $_;
			unless(defined $optionLabels->{"$optionName"}){
				 $invalidOptionName = $invalidOptionName." ".$optionName;
			}
		}

		if($invalidOptionName ne '') {
			print "[Warning] There is invalid options for searching default value - $invalidOptionName \n";
			print "\n";
			return 0;
		}
	} else {
		return 0;
	}

	return 1;
}

sub checkDefaultConditions {
	my ( $ec, $projectName, $moduleName ) = @_; 

	my $conditions = getPropertiesRefRecursively($ec, "/myProject/mdList/$moduleName/default");
	unless( defined $conditions ) {
		return 0;
	}

	my $optionList = CHECK_EF_FUNC_ERROR($ec, "getFormalParameterOptionList", $projectName, "CO_Set_Options");

	foreach ( sort keys %{$conditions} ){
		my $index = $_;
		my $conditionJson = $conditions->{$index};
		my $condition = getHashFromJsonText($conditionJson);

		unless( defined $condition->{"rule"} ) {
			next;
		}

		my @newRuleItem = ();
		my @rules = @{$condition->{"rule"}};
		foreach ( @rules ) {
			my $rule = $_;
			my $optionName = $rule->{'lhs'};
			my $optionValue = $rule->{'rhs'};
			my $isValid = 1; 

			unless(defined $optionList->{"$optionName"}{"list"} ) {
				next;
			}

			unless(defined $optionList->{"$optionName"}{"list"}{"$optionValue"}) {
				print "[Warning] There is invalid options for searching default value.\n";
				print "\tOptionName : $optionName\n";
				print "\tOptionValue : $optionValue\n";
				print "\n";
				return 0;
			} 
		} 
	}

	return 1;
}

sub getDefaultModuleInfo {
	my ($ec, $projectName, $optionRef, $moduleName) = @_;

	my %defaultModuleInfo = ();
	$defaultModuleInfo{'moduleVersionDefault'} = '';
	$defaultModuleInfo{'releaseVersionDefault'} = '';
	$defaultModuleInfo{'releaseStartDefault'} = '';
	$defaultModuleInfo{'releaseEndDefault'} = '';
	$defaultModuleInfo{'releaseCheckDefault'} = '';	

	unless(checkDefaultOptionList($ec, $moduleName)) {
		return undef;
	}

	unless(checkDefaultConditions($ec,  $projectName, $moduleName)) {
		return undef;
	} 

	my $defaultRef = getPropertiesRefRecursively($ec, "/myProject/mdList/$moduleName/");
	unless ( defined $defaultRef ) {
		return undef;
	}

	my $optListStr = $defaultRef->{'OptionList'};
	my $ruleRefs = $defaultRef->{'default'};

	my %ruleIndexs = ();
	my @rules = ();
	my @ruleIndexs = ();
	my @conditions = split /\|/, $optListStr;
	my $all = undef;
	my $count = 0;

	foreach ( sort keys %{$ruleRefs} ){
		my %rule = ();
		my $ruleIndex = $_;
		my $expressionRef = getHashFromJsonText($ruleRefs->{$ruleIndex});
		unless(defined $expressionRef->{'rule'}) {
			$all = $expressionRef;
			next;
		}

		foreach (@{ $expressionRef->{'rule'} }) {
			my $optName = $_->{'lhs'};
			my $propertyValue = $optionRef->{"$optName"};
			my $value = $_->{'rhs'};
			my $operation = $_->{'operation'};
			my $andOrOperation = $_->{'andOrOperation'};

			my %subExpression = ();
			$subExpression{'PropertyValue'} = $propertyValue;
			$subExpression{'Value'} = $value;
			$subExpression{'Operation'} = $operation;
			$subExpression{'AndOrOperation'} = $andOrOperation; 
			$rule{"$optName"} = \%subExpression; 
		}
		push(@ruleIndexs, $ruleIndex);
		push(@rules,\%rule);
	}

	my $ruleSelector = new RuleSelector();
	$ruleSelector->addKeys(\@conditions);
	$ruleSelector->addRules(\@rules);
	my $selectedRuleIndex = $ruleSelector->searchAppropriateRuleAmongVariousRules();

	my $type = 'NONE';
	my $defaultRuleInfo = undef;
	if(defined $selectedRuleIndex) {
		$type = "NORMAL";
		$defaultRuleInfo = getHashFromJsonText($ruleRefs->{$ruleIndexs[$selectedRuleIndex]});
	}elsif(defined $all && !$ruleSelector->hasConflict()) {
		$type = "ALL";
		$defaultRuleInfo = $all;
	}

	if(defined $defaultRuleInfo) {
		if(defined $defaultRuleInfo->{'moduleVersion'}) {
			$defaultModuleInfo{'moduleVersionDefault'} = $defaultRuleInfo->{'moduleVersion'};
		}
		if(defined $defaultRuleInfo->{'releaseVersion'}) {
			$defaultModuleInfo{'releaseVersionDefault'} = $defaultRuleInfo->{'releaseVersion'};
		}
		if(defined $defaultRuleInfo->{'releaseStart'}) {
			$defaultModuleInfo{'releaseStartDefault'} = $defaultRuleInfo->{'releaseStart'};
		}		
		if(defined $defaultRuleInfo->{'releaseEnd'}) {
			$defaultModuleInfo{'releaseEndDefault'} = $defaultRuleInfo->{'releaseEnd'};
		}	
 		if(defined $defaultRuleInfo->{'releaseCheck'}) {
			$defaultModuleInfo{'releaseCheckDefault'} = $defaultRuleInfo->{'releaseCheck'};
		}
	} else {
		if($ruleSelector->hasConflict()){
			$type = "CONFLICT";
			$defaultModuleInfo{'moduleVersionDefault'} = "$[/myProject/errorCode/DefaultResultCode/ruleHasConflict]";
		}
	}

	$defaultModuleInfo{'RuleType'} = $type;

	return \%defaultModuleInfo;
}

sub internalHandlefix {
    my $val = shift; 
    $val =~ s/\\\\/\//g;
    $val =~ s/\\/\//g;
    $val =~ s/"//g;
    return $val;
}

sub internalCheckConditionIn {        
    my %filterArray = map { ( $_, 1 ) } @{ $_[0] };
    my @not_in_set = grep { not $filterArray{$_} } @{ $_[1] };


    my $count = @not_in_set;
    if ( $count > 0 ) { 
        return 0;
    }

    return 1;
}

sub internalCheckConditionNotIn {
    my %filterArray = map { ( $_, 1 ) } @{ $_[0] };
    my @not_in_set = grep { not $filterArray{$_} } @{ $_[1] };

    my $count = @not_in_set;
    if ( $count > 0 ) {
        return 1;
    }

    return 0;
}

sub internalCheckConditionInWithStr {
    $_[0] = substr( $_[0], 1, length( $_[0] )-2) ;
    $_[1] = substr( $_[1], 1, length( $_[1] )-2) ; 

    my @values = split( ',', $_[0] );
    my @filters = split( ',', $_[1] );

    return internalCheckConditionIn( \@filters, \@values ); 
}

sub internalCheckConditionNotInWithStr {
    $_[0] = substr( $_[0], 1, length( $_[0] )-2) ;
    $_[1] = substr( $_[1], 1, length( $_[1] )-2) ;
    my @filters = split( ',', $_[0] );
    my @values = split( ',', $_[1] );

    return internalCheckConditionNotIn( \@filters, \@values ); 
}

sub internalGuessEncoding {
    my $str = shift;
    my $enc = guess_encoding( $str, qw/euc-kr cp949 utf8/ );
    if ( ref($enc) ) { 
        $str = decode( $enc->name, $str ); 
    } else {
        print "Error : Failed to guess_encoding of - $str\n";
        exit;
    }

    return $str;
}

sub internalCheckWhetherContaninsStringInFile { 
    my $filePaths = shift;
    my $contents = shift;
    $contents =~ s/"//g;

    $filePaths = internalHandlefix($filePaths);

    open(FILE, "< ". "$filePaths") or die "Can't open $filePaths : $!\n" ;

    while (<FILE>)
    {
     my $token = internalGuessEncoding($_);
        if ($token =~ /$contents/) {
            return 1;
        }
    }

    return 0;
}

#########################################
# Expression Solver
#########################################

my @tokens;
my @token_def = (
    [ Whitespace => qr{\s+}, 1 ],
    [ Comment => qr{#.*\n?$}m, 1 ],
    [ TermOp => qr{!=|==|<=|>=|>|<|\|\||in|ni|fc} ],
    [ FactorOp => qr{\&\&} ],
    [ Number => qr{\d+} ],
    [ Character => qr{"(.+?)"} ],
    [ OpenParen => qr{\(} ],
    [ CloseParen => qr{\)} ],
);

sub tokenize {
    my $input = shift;
    pos($input) = 0;

    @tokens = ();

    while ( pos($input) < length $input ) {
        my $matched = 0;
        for my $t (@token_def) {
            my ( $name, $re, $ignore_flag ) = @$t;
            if ( $input =~ m/\G($re)/gc ) {
                $matched = 1;
                next if $ignore_flag;
                push @tokens, [ $name, $1 ];
                next;
            }
        }
        die "Syntax error at postion " . pos($input) unless $matched;
    }
}

sub match {
    my $expected_token = shift;
    if ( $tokens[0][0] eq $expected_token ) {
        my $current = shift @tokens;

        return $current->[1];
    }
    else {
        die "Syntax error: expected $expected_token, got $tokens[0][0]\n";
    }
}

sub expression {
    my @res = ( term() );
    while ( lookahead('TermOp') ) {
        push @res, match('TermOp');
        push @res, term();
    }

    return \@res;
}

sub term {
    my @res = ( factor() );
    while ( lookahead('FactorOp') ) {
        push @res, match('FactorOp');
        push @res, factor();
    }

    return \@res;
}

sub factor {
    if ( lookahead('OpenParen') ) {
        match('OpenParen');
        my $res = expression();
        match('CloseParen');

        return $res;
    }
    else {
        atom();
    }
}

sub type {
    my $expected_token = shift;
    if ( $tokens[0][0] eq $expected_token ) {
        return 1;
    }
    else {
        return 0;
    }
}

sub lookahead {
    my @expected = @_;
    no warnings 'uninitialized';
    for ( 0 .. $#expected ) {
        return 0 if $tokens[$_][0] ne $expected[$_];
    }

    return 1;
}

sub atom {
    if ( &type('Number') ) {
        return &match('Number');
    }

    if ( &type('Character') ) {
        return &match('Character');
    }

    die "Syntax error: unexpected atom exists.\n";
}

sub execute {
    my $tree = shift;
    return $tree unless ref $tree;
    my %ops = (
        '==' => sub { if( $_[0] eq $_[1] ) { return 1; } return 0;},
        '!=' => sub { if( $_[0] ne $_[1] ) { return 1; } return 0;},
        '<' => sub { if( $_[0] lt $_[1] ) { return 1; } return 0;},
        '<=' => sub { if( $_[0] le $_[1] ) { return 1; } return 0;},
        '>' => sub { if( $_[0] gt $_[1] ) { return 1; } return 0;},
        '>=' => sub { if( $_[0] ge $_[1] ) { return 1; } return 0;},
        '&&' => sub { if( $_[0] && $_[1] ) { return 1; } return 0;},
        '||' => sub { if( $_[0] || $_[1] ) { return 1; } return 0;},
        'in' => sub { if(internalCheckConditionInWithStr($_[0],$_[1])){ return 1; } return 0;},
         'ni' => sub { if(internalCheckConditionNotInWithStr($_[0],$_[1])){ return 1; } return 0;},
        'fc' => sub { if(internalCheckWhetherContaninsStringInFile($_[0],$_[1])){ return 1; } return 0;},
    );

    my ( $val, @rest ) = @$tree;
    $val = execute($val);
    while (@rest) {
        my $op = shift @rest;
        my $next = execute( shift @rest );
        $val = $ops{$op}->( $val, $next );
    }

    return $val;
}

sub solveBooleanExpression{
    my $input = shift;

    tokenize($input); 

    my $parse_tree = expression();
    my $result = execute($parse_tree);


    if($result > 0 ) { 
        return 1;    
    } else { 
        return 0;
    } 
}

sub solveRegularExpression{
    my $content = shift;
    my $pattern = shift; 

    my $re = qr/(?ms:$pattern)/; 
       my @matches = ( $content =~ m/$re/g );

    my $arrSize = @matches;
    if ( $arrSize > 0 ) { 
        unless ( length $matches[0] ){
            return "";
        }

        return $matches[0];
    }

    return "";
}

sub createJobLink {
    my $ec = shift;
    my $title = shift;
    my $link= shift;
    #$ec->setProperty("/myJob/report-urls/$title", $link);

	my($success, $xPath, $errMsg, $errCode)= invokeCommand($ec, "SuppressLog IgnoreError", "setProperty",{ propertyName => "/myJob/report-urls/$title", value => "$link"});
	if (! $success) {
		printf(" Error createJobLink - %s", $link);
		printf(" %s: %s\n", $errCode, $errMsg);
		return 0;
	} 

	return 1;
}

sub createJobStep {
    my ($ec, $subProcedureProjectName, $subProcedureName, $jobStepName, $actualParameters) = @_;

    if(defined $actualParameters) {
        my $xPathParams = $ec->getFormalParameters( {'projectName' => $subProcedureProjectName, 'procedureName' => $subProcedureName});
        my @nodes = $xPathParams->findnodes("//formalParameter");
        my @formalParamLists = (); 
        foreach my $node (@nodes) { 
            my $paramName = $node->findvalue("formalParameterName");
            unless(defined $actualParameters->{"$paramName"}){
                my $actualParameterValue = $node->findvalue("defaultValue");
                $actualParameters->{"$paramName"} = "$actualParameterValue";
            }
        }
    }

    my @actualParameterAOH = ();

    my $success = undef;
    my $xPath = undef;
    my $errMsg = undef;
    my $errCode = undef; 

    if(defined $actualParameters) {
        foreach my $actualParameterName ( sort keys %{$actualParameters} )
        {
            my $actualParameterValue = $actualParameters->{$actualParameterName};
            push @actualParameterAOH, { 'actualParameterName' => "$actualParameterName", 'value' => "$actualParameterValue"};
        }

        ($success, $xPath, $errMsg, $errCode)= invokeCommand($ec, "SuppressLog IgnoreError", "createJobStep", {  'subproject' => "$subProcedureProjectName", 'subprocedure' => "$subProcedureName", 'jobStepName' => "$jobStepName", 'actualParameter' => \@actualParameterAOH });
        if (! $success) {
            printf(" Error createJobStep - subProcedureName : %s , jobStepName : %s", $subProcedureName, $jobStepName);
            printf(" %s: %s\n", $errCode, $errMsg);
            return 0;
        }

    } else {
        ($success, $xPath, $errMsg, $errCode)= invokeCommand($ec, "SuppressLog IgnoreError", "createJobStep", {  'subproject' => "$subProcedureProjectName", 'subprocedure' => "$subProcedureName", 'jobStepName' => "$jobStepName"});
        if (! $success) {
            printf(" Error createJobStep - subProcedureName : %s , jobStepName : %s", $subProcedureName, $jobStepName);
            printf(" %s: %s\n", $errCode, $errMsg);
            return 0;
        } 
    }

    return 1;
}

