 sub createMultiIntegrationLinkToJob {
    my ($ec, $projectName, $workflowDefName, $linkText) = @_;
    my $encodedProjectName = uri_escape($projectName);
    my $encodedWorkflowDefName = uri_escape($workflowDefName);

    my $propValue = "/commander/pages/MultiIntegration/MultiIntegration_run?tb/2/projName/$encodedProjectName/wfDefName/$encodedWorkflowDefName/"; 

    my($success, $xPath, $errMsg, $errCode)= invokeCommand($ec, "SuppressLog IgnoreError", "setProperty","/myJob/report-urls/$linkText", "$propValue");
    if (! $success) {
        printf(" Error createMultiReIntegrationLinkToWorkflow - %s => %s\n", "report-urls/$linkText", "$propValue");
        printf(" %s: %s\n", $errCode, $errMsg);
        return 0;
    } 

    return 1;
}

sub createRunProcedureLinkToJob {
    my ($ec, $projectName, $procedureName, $linkText, $actualParametersRef) = @_;
    my $encodedProjectName = uri_escape($projectName);
    my $encodedProcedureName = uri_escape($procedureName);
    my $actualParametersString = "";


   print Dumper(%$actualParametersRef);

    my $paramTotal = scalar keys %$actualParametersRef;
    if ($paramTotal) {
        $actualParametersString = "&numParameters=$paramTotal";
        my $paramIndex = 1;
        foreach my $paramName (keys %$actualParametersRef) {
            my $paramValue = uri_escape($actualParametersRef->{$paramName});
            $actualParametersString .= 
                            "&parameters${paramIndex}_name=$paramName".
                            "&parameters${paramIndex}_value=$paramValue";
            $paramIndex++;
        }
    }

	my $propValue =  "/commander/runProcedure.php?runNow=1&projectName=$encodedProjectName&procedureName=$encodedProcedureName$actualParametersString";		 

	my($success, $xPath, $errMsg, $errCode)= invokeCommand($ec, "SuppressLog IgnoreError", "setProperty",
															{'propertyName' => "report-urls/$linkText", 
															 'value' => "$propValue", 
															 'jobId'=>$ENV{COMMANDER_JOBID}});
	if (! $success) {
		printf(" Error createRunProcedureLinkToJob - %s => %s\n", "report-urls/$linkText", "$propValue");
		printf(" %s: %s\n", $errCode, $errMsg);
		return 0;
	} 

	return 1;

}

sub deleteJobAndWorkspace {
	my ($ec, $jobId) = @_;

    my ($success, $xPath, $errMsg, $errCode) = invokeCommand("SuppressLog", "getJobInfo", $jobId);
	if (! $success) {
		printf(" Error deleteJobAndWorkspace(getJobInfo) - JobID : %s", $jobId);
		printf(" %s: %s\n", $errCode, $errMsg);
		return 0;
	} 

	my $osIsWindows = $^O =~ /MSWin/;											 
	my $wsNodeset = $xPath->find('//job/workspace');

    foreach my $wsNode ($wsNodeset->get_nodelist) {

        my $workspace;
        if ($osIsWindows) {
            $workspace = $xPath->findvalue('./winUNC', $wsNode);
            $workspace =~ s'/'\\'g;
        } else {
            $workspace = $xPath->findvalue('./unix', $wsNode);
        }

        #print "Workspace:\t$workspace\n";

        if ($workspace =~ /[-_][\d]+$|[-_][0-9a-f]{8}(?:-[0-9a-f]{4}){4}[0-9a-f]{8}$/) {
			rmtree ([$workspace]);
            print "Deleting Workspace:\t$workspace\n";
        }
    }

    ($success, $xPath, $errMsg, $errCode)= invokeCommand($ec, "SuppressLog IgnoreError", "deleteJob", $jobId);
	if (! $success) {
		printf(" Error deleteJobAndWorkspace(deleteJob) - JobID : %s", $jobId);
		printf(" %s: %s\n", $errCode, $errMsg);
		return 0;
	}

	return 1;
}

