sub retryTimedOutJobStep {
	my $ec = shift;

    my($success, $xPath, $errMsg, $errCode)= invokeCommand($ec, "SuppressLog IgnoreError", "findJobSteps",
														{'jobId'=>$ENV{COMMANDER_JOBID}
														 filter => [{ "propertyName"=>"errorCode",
																	   operator =>"equals",
																	   operand1 => "TIMEOUT"}]
														 }));
    if (! $success) {
        print(" Error retryTimedOutJobStep\n");
        printf(" %s: %s\n", $errCode, $errMsg);
        return 0;
    }

	#print "Return data from Commander:\n" . $xPath-> findnodes_as_string("/"). "\n";

	my $nodeset=$xPath->find("//jobStep");
	unless(defined $nodeset) {
		return 1;
	}

	foreach my $node ($nodeset->get_nodelist) {
		my $stepName=$xPath->findvalue('stepName', $node);
		printf("Target step name which timed out : %s \n", $stepName);
		my $command=$xPath->findvalue('command', $node);
	    ($success, $xPath, $errMsg, $errCode)= invokeCommand($ec, "SuppressLog IgnoreError", "createJobStep", { 'command'=>$command, 'jobStepName' => $stepName."-retry"});
        if (! $success) {
            printf(" Error retryTimedOutJobStep -  jobStepName : %s\n", $jobStepName);
            printf(" %s: %s\n", $errCode, $errMsg);
            return 0;
        } 
	}

	return 1;
}

