sub createCheckBoxParam {
    my $ec = shift;
    my $paramName= shift;
    my $projectName = shift;
    my $procedureName = shift;
	my $description = shift;

    my $required = shift; # 0 or 1
    my $defaultValue = shift; # 1 or 0

    if($defaultValue ne '') {
        invokeCommand($ec," SuppressLog IgnoreError " , 'createFormalParameter' ,
						$projectName, $procedureName, $paramName, 
						 {'description' => $description,
                          'type' => 'checkbox',
                          'required' => $required,
                          'defaultValue' => $defaultValue
                         }); 

    } else {
        invokeCommand($ec," SuppressLog IgnoreError " , 'createFormalParameter' ,
						$projectName, $procedureName, $paramName, 
						 {'description' => $description,
                          'type' => 'checkbox',
                          'required' => $required,
                          'defaultValue' => $defaultValue
                         }); 
    }

	$ec -> setProperty( "/projects/$projectName/procedures/$procedureName/ec_customEditorData/parameters/$paramName/checkedValue" , '1');
	$ec -> setProperty( "/projects/$projectName/procedures/$procedureName/ec_customEditorData/parameters/$paramName/uncheckedValue" , '0');
	$ec -> setProperty( "/projects/$projectName/procedures/$procedureName/ec_customEditorData/parameters/$paramName/initiallyChecked" , ( $defaultValue eq 'true' )?1:0);
}

sub createEntryParam {
    my ($ec, $paramName, $projectName, $procedureName, $description, $required, $defaultValue) = @_;

	my %params = ();
	$params{'description'} = "$description";
	$params{'type'} = "entry";
	$params{'required'} = $required;
	if(defined $defaultValue && $defaultValue ne "") {
		$params{'defaultValue'} = $defaultValue;
	}

    my($success, $xPath, $errMsg, $errCode)= invokeCommand($ec," SuppressLog IgnoreError " , 'createFormalParameter' ,
														   $projectName, $procedureName, $paramName, \%params); 
    if (! $success) {
        printf(" Error createEntryParam - %s", $paramName);
        printf(" %s: %s\n", $errCode, $errMsg);
        return 0;
    } 

    $ec->setProperty( "/projects/$projectName/procedures/$procedureName/ec_customEditorData/parameters/$paramName/formType" , "standard");

    return 1;
}

