$|=1;

sub new 
{
	my $class = shift;
	my ($self) = {};
	bless $self, $class;


	my $ec = new ElectricCommander();  

	$self->{'_ec'} = $ec;
	$self->{'_protoTypeMode'} = 0;
	$self->{'_projectPaths'} = []; 
	$self->{'_userPaths'} = []; 
	$self->{'_groupPaths'} = []; 
	$self->{'_workspacesPaths'} = []; 
	$self->{'_poolPaths'} = []; 
	$self->{'_resoucrePaths'} = []; 
  	$self->{'_artifactPaths'} = []; 

   return $self; 
}

####### External subRoutines###########################

sub scanDirectory {
    my ($self, $path) = @_;

	$path = internalHandlefix($path);

	unless (-d $path ){  # is this a  file?

		unless(internalCheckXMLForm($path) ) {
			return; 
		} 

		if(internalStartWithPools($path)) { push (@{$self->{'_poolPaths'}}, $path);}
		elsif(internalStartWithGroups($path)){ push (@{$self->{'_groupPaths'}}, $path)}
		elsif(internalStartWithProjects($path)){ push (@{$self->{'_projectPaths'}}, $path)}
		elsif(internalStartWithResources($path)){ push (@{$self->{'_resoucrePaths'}}, $path)}
		elsif(internalStartWithWorkspaces($path)){ push (@{$self->{'_workspacesPaths'}}, $path)}
		elsif(internalStartWithUsers($path)){ push (@{$self->{'_userPaths'}}, $path);}
		elsif(internalStartWithArtifacts($path)){ push (@{$self->{'_artifactPaths'}}, $path);}
		return;
    }

    opendir my $dh, $path or die;
    while (my $sub = readdir $dh) {
        next if $sub eq '.' or $sub eq '..';

        $self->scanDirectory("$path/$sub");
    }

    close $dh;
    return;
}

sub importDatas { 
	my ($self, $key, $arrayRef ) = @_;
	my $errorCount=0;
	my $successCount=0; 

	print "\n";
	print "$key ---------------------------------------------------- \n";
	print "\n";

	foreach my $path (@{$arrayRef}) {
		my $name = basename($path,".xml"); 
		my $enc = guess_encoding( $name, qw/cp949 utf8/ );

		print "\t $name \n";

		if ( ref($enc) ) { 
			$name = decode( $enc->name, $name ); 
		} else {
			print "Error : Failed to guess_encoding of fileName - $name\n";
			exit;
		}

	 	unless($self->{'_protoTypeMode'}) {		 
			my ($success, $res, $errMsg, $errCode) =  $self->internalInvokeCommand("SuppressLog IgnoreError", "import", {file => $path, path => "/$key/".$name, force => 1} );
			if (! $success) {
				printf("  Error importing %s", $path);
				printf("  %s: %s\n", $errCode, $errMsg);
				$errorCount++;
			} else {
				$successCount++;
			}
		}
	}

	print "\n";
	print "  $key : $successCount imported.\n";
	print "  $key : $errorCount failed.\n";
}

sub importResourcePools { 
	my ($self) = @_;
	$self->importDatas('resourcePools',$self->{'_poolPaths'});
}

sub importProjects { 
	my ($self) = @_;
	$self->importDatas('projects',$self->{'_projectPaths'});
}

sub importResources { 
	my ($self) = @_;
	$self->importDatas('resources',$self->{'_resoucrePaths'});
}

sub importGroups { 
	my ($self) = @_;
	$self->importDatas('groups',$self->{'_groupPaths'});
}

sub importWorkspaces { 
	my ($self) = @_;
	$self->importDatas('workspaces',$self->{'_workspacesPaths'});
}

sub importUsers { 
	my ($self) = @_;
	$self->importDatas('users',$self->{'_userPaths'});
}

sub importArtifacts { 
	my ($self) = @_;
	$self->createArtifactsByXML('artifacts',$self->{'_artifactPaths'});
}


sub createArtifactsByXML { 
	my ($self, $key, $arrayRef ) = @_;
	my $errorCount=0;
	my $successCount=0; 

	print "\n";
	print "$key ---------------------------------------------------- \n";
	print "\n";

	foreach my $path (@{$arrayRef}) {
		my $name = basename($path,".xml"); 
		my $enc = guess_encoding( $name, qw/cp949 utf8/ );

		print "\t $name \n";

		if ( ref($enc) ) { 
			$name = decode( $enc->name, $name ); 
		} else {
			print "Error : Failed to guess_encoding of fileName - $name\n";
			exit;
		}

	 	unless($self->{'_protoTypeMode'}) { 
			my $xPath = XML::XPath->new(filename => $path);
			my $artifactName .= $xPath->findvalue('/exportedData/artifact/artifactName');
			my $artifactKey .= $xPath->findvalue('/exportedData/artifact/artifactKey');
			my $artifactVersionNameTemplate = $xPath->findvalue('/exportedData/artifact/artifactVersionNameTemplate');
			my $description = $xPath->findvalue('/exportedData/artifact/description');
			my $groupId = $xPath->findvalue('/exportedData/artifact/groupId');

			my ($success, $res, $errMsg, $errCode) =  
						$self->internalInvokeCommand("SuppressLog IgnoreError", "createArtifact", 
							{groupId => $groupId, 
							artifactKey => $artifactKey, 
							description => $description, 
							artifactVersionNameTemplate => $artifactVersionNameTemplate} );

			if (! $success) {
				printf("  Error createArtifact %s", $path);
				printf("  %s: %s\n", $errCode, $errMsg);
				$errorCount++;
			} else {
				$successCount++;
			}

			foreach my $node ($xPath->find('//aclEntry')->get_nodelist) { 
				my $principalName = $node->find('./principalName')->string_value; 
				my $principalType = $node->find('./principalType')->string_value; 
				#print ("PrincipalName : $principalName \n");
				#print ("PrincipalType : $principalType \n");

				my $allowChangePermissions =  "deny";
				my $allowExecute = "deny";
				my $allowModify = "deny";
				my $allowRead =  "deny";

				if($node->find('./allowChangePermissions')->string_value() eq '1' ) {
					 $allowChangePermissions = "allow";
				}

				if($node->find('./allowExecute')->string_value() eq '1' ) {
					 $allowExecute = "allow";
				}

				if($node->find('./allowModify')->string_value() eq '1' ) {
					 $allowModify = "allow";
				}

				if($node->find('./allowRead')->string_value() eq '1' ) {
					 $allowRead = "allow";
				}

				#print ("allowChangePermissions : $allowChangePermissions \n");
				#print ("allowExecute : $allowExecute \n");
				#print ("allowModify : $allowModify \n");
				#print ("allowRead : $allowRead \n");

				($success, $res, $errMsg, $errCode) =  
					$self->internalInvokeCommand("SuppressLog IgnoreError", "createAclEntry", 
							{principalType => $principalType, 
							principalName => $principalName, 
							artifactName=> $artifactName,
							readPrivilege=>$allowRead, 
							modifyPrivilege=>$allowModify, 
							executePrivilege =>$allowExecute,
							changePermissionsPrivilege =>$allowChangePermissions});

				if (! $success) {
					printf("  Error acl loading - %s", $path);
					printf("  %s: %s\n", $errCode, $errMsg);

				}
			}

		}

	}

	print "\n";
	print "  $key : $successCount imported.\n";
	print "  $key : $errorCount failed.\n";
}



####### Internal subRoutines###########################

sub internalInvokeCommand {
	my $self = shift;
    my $optionFlags = shift;
    my $commanderFunction = shift;
	my $ec = $self->{'_ec'};

    my $xPath;
    my $success = 1;

    my $bSuppressLog = $optionFlags =~ /SuppressLog/i;
    my $bSuppressResult = $bSuppressLog || $optionFlags =~ /SuppressResult/i;
    my $bIgnoreError = $optionFlags =~ /IgnoreError/i;


    $ec->abortOnError(0) if $bIgnoreError;
    $xPath = $ec->$commanderFunction(@_);
    $ec->abortOnError(1) if $bIgnoreError;

    # Check for error return
    my $errMsg = $ec->checkAllErrors($xPath);
    my $errCode=$xPath->findvalue('//code',)->value();
    if ($errMsg ne "") {
        $success = 0;
    }
    if ($xPath) {
        print "Return data from Commander:\n" .
               $xPath->findnodes_as_string("/") . "\n"
            unless $bSuppressResult;
    }

    # Return the result
    return ($success, $xPath, $errMsg, $errCode);
} 

sub internalHandlefix {
    my $val = shift; 
	$val =~ s/\\\\/\//g;
	$val =~ s/\\/\//g;
	$val =~ s/"//g;
    return $val;
}

 sub internalCheckXMLForm  { 
	my $path = shift;
	if (substr($path, -3) eq 'xml') {
		return 1;
	}

	return 0; 
}

