sub checkPoolResourcesAlive {
    my ($ec, $resPoolName) = @_;

    my($success, $result, $errMsg, $errCode) = invokeCommand($ec, "SuppressLog IgnoreError", "getResourcesInPool", $resPoolName);
    if (! $success) {
        printf(" Error checkPoolResourcesAlive \n");
        printf(" %s: %s\n", $errCode, $errMsg);
        return undef;
    } 

	#print $result->findnodes_as_string('/') . "\n";


    my $nodeset=$result->find('//resource/agentState');

    my $resultFlag = 1;
    my %resurceAlive = ();
    foreach ($nodeset->get_nodelist) {
        my $resourceName=$result->findvalue('../resourceName', $_);
        my $alive=$result->findvalue('alive', $_);

		if("$alive" ne "1") {
			$resultFlag = 0;
			print "Unavailable Resource : $resourceName \n"; 
		}
    }

    return $resultFlag;
}

sub getResAliveResNameFromPool {
    my ($ec, $resPoolName) = @_;

    my($success, $result, $errMsg, $errCode) = invokeCommand($ec, "SuppressLog IgnoreError", "getResourcesInPool", $resPoolName);
    if (! $success) {
        printf(" Error getResAliveHostNamesFromPool \n");
        printf(" %s: %s\n", $errCode, $errMsg);
        return undef;
    } 

	#print $result->findnodes_as_string('/') . "\n";


    my $nodeset=$result->find('//resource/agentState');

	my @resNamesAlive = ();
	my %hostNamesAliveMap = ();

    foreach ($nodeset->get_nodelist) {
        my $hostName=$result->findvalue('../hostName', $_);
		my $resourceName=$result->findvalue('../resourceName', $_);
        my $alive=$result->findvalue('alive', $_);

		if (exists $hostNamesAliveMap{$hostName}) {
			next;
		}

		if("$alive" eq "1") {
			push @resNamesAlive, "$resourceName";
		}

        print "ResoureName: $resourceName (HostName: $hostName) , Alive : $alive \n";  

		$hostNamesAliveMap{$hostName} = 1;
    }

    return \@resNamesAlive;
}

sub checkScheduleExist {
	my ($ec, $projectName, $scheduleName) = @_;

	 my($success, $xPath, $errMsg, $errCode)= invokeCommand($ec, "IgnoreError", "getSchedules", "$projectName");
	 if (!$success)
	 {
		print(" Error checkScheduleExist- $scheduleName\n");
		print(" $errCode: $errMsg\n");
		return 0;
	 }

     my @schedsEC = $xPath->findnodes("//schedule");
     foreach my $sched (@schedsEC) {
        my $schedName = $sched->findvalue("scheduleName")->value;
		if($schedName eq "$scheduleName" ) {
			return 1;
		}
    }

	print(" Error checkScheduleExist- $scheduleName\n");
	print(" \t There is no specified schedule.\n");
	return 0;	
}

