sub getProjectScheduleNames {
	my ($ec, $projectName) = @_;

	 my($success, $xPath, $errMsg, $errCode)= invokeCommand($ec, "IgnoreError", "getSchedules", "$projectName");
	 if (!$success)
	 {
		print(" Error getProjectScheduleNames- $scheduleName\n");
		print(" $errCode: $errMsg\n");
		return 0;
	 }

	 my %scheduleNamesRef = ();
     my @schedsEC = $xPath->findnodes("//schedule");
     foreach my $sched (@schedsEC) {
        my $schedName = $sched->findvalue("scheduleName")->value;
		$scheduleNamesRef{"$schedName"} = "$schedName";
    }

	return \%scheduleNamesRef;
}

sub arrayRefToStr {
    my $arrayRef = shift;
	unless(defined $arrayRef ){
		return '';
	}

	my $size = @{$arrayRef};
	if( $size <= 0) {
		return '';
	}

    my $s = join '|', @{$arrayRef};
    $s =~ s/^\s+|\s+$//g; 
    return $s;
}

sub beginWith
{
    return substr($_[0], 0, length($_[1])) eq $_[1];
}

sub checkExistFileOrDir{
    my ($path) = @_;
    if(-d $path || -e $path) { return 1;}
    return 0;
}

sub checkHasSameSizeArray {
    my ($arr1, $arr2) = @_;
    my %counts = ();
    $counts{$_} += 1 foreach (@$arr1);
    $counts{$_} -= 1 foreach (@$arr2);
    return !(grep { $_ != 0 } values %counts);
}

sub checkItemExistInArray {
	my $arrayRef = shift;
	my $item = shift;
	if(grep( /^$item$/, @{$arrayRef})) {
		return 1;
	}

	return 0;
}

sub copyFile {
    my ($srcFile, $destFile, $replaceIfExists) = @_;

	$srcFile = <<'EOF';
	$srcFile
EOF

	$destFile = <<'EOF';
	$destFile
EOF

	chomp($srcFile);
	chomp($destFile);


    if ($srcFile =~ /["']/) {
        die("Source file name $srcFile contains an illegal quote character\n");
    }

    if ($destFile =~ /["']/) {
        die("Destination file name $destFile contains an illegal quote " .
            "character\n");
    }

    if (!$replaceIfExists && -e $destFile && ! -d $destFile) {
        die("Destination file $destFile already exists.\n");
    }

    if ($replaceIfExists && -d $destFile) {
        pathempty($destFile) 
            or die("Couldn't clear $destFile prior to copy: $!\n");
    }

    my @parts = File::Spec->splitdir($srcFile);
    if ($parts[$#parts] =~ /\*/) {
        if (-e $destFile) {
            if (! -d $destFile) {
                die("Destination file $destFile already exists and is not " .
                    "a directory.\n");
            }
        } else {
            eval {
                mkpath $destFile;
            };
            die("Couldn't create destination directory: $@\n") if $@;
        }
    } else {
        die("Source file $srcFile does not exist.\n") if (! -e $srcFile);
    }

    $File::Copy::Recursive::CPRFComp = 1;

    for my $path (bsd_glob($srcFile)) {
        rcopy($path, $destFile) or die . "Copy failed: $!\n";
    }

    return;
}

sub createDirectoryIfNotExists {
	my $path = shift; 
        my $perm = shift;

	if (! -d $path) {
		unless(system ("mkdir -p $path -m $perm 2> /dev/null") == 0){
			return 0;
		}
	}

	return 1;
}

sub deleteDirectory{
    my ($path, $recursive) = @_;

    if($recursive && $recursive eq "1"){
        rmtree("$path") or die "Fail to delete $path";
    }else{
        rmdir("$path") or  die "Fail to delete $path";
    }
}

sub endsWith {
    my $lenStr = length($_[0]);
    my $lenSuffix = length($_[1]); 
    return substr($_[0], $lenStr - $lenSuffix, $lenSuffix) eq $_[1];
}

sub findDirectories {
    my $path = shift;

    my @directories = ();

    $path =~ s/\\/\//g;
    $path =~ s/\/\//\//g;

    if(-d $path){
		if (!opendir(DIR, $path)) {
			return undef;
		}

        while(my $dir = readdir(DIR)) {
			$dir = decode( 'utf-8', $dir);
            if(-d "$path$dir") {
                next if(($dir eq ".") or ($dir eq ".."));
				#print "DIRECTORY PATH : $path$dir\n";
                push @directories, "$dir";
            }
        }        
    }else{
        return undef;
    }

    closedir(DIR);

    my $count = @directories;
    if($count == 0) {
        return undef;
    }

    return \@directories;

}

sub findFiles {
    my $path = shift;
    my $fileExtention = shift;
    my @files = ();

    $path =~ s/\\/\//g;
    $path =~ s/\/\//\//g;
    #opendir(DIR, $path) or die $!;

    if (!opendir(DIR, $path)) {
        return undef;
    }

    while(my $file = readdir(DIR)) {
		$file = decode( 'utf-8', $file);
        if(-f "$path$file") {
            next if(($file eq ".") or ($file eq ".."));
             # endWithExtention
            if($file =~ /$fileExtention$/) {
                push @files, "$file";
            }
        }
    }

    closedir(DIR);

    my $count = @files;
    if($count == 0) {
        return undef;
    }

    return \@files;
}

sub getArrayItemsByNewLine {
	my $arrayName = shift;
	my $strArray = shift;

	$strArray =~ s/^\s+|\s+$//g;

	my @items = grep { $_ ne '' } split(/\n/, $strArray);

	if (! @items) { 
		print "Please, check ur $arrayName input values.\n";
		print "(There is no elements in the $arrayName..)\n";
		exit 1;
	}elsif (grep ! /^\s*$/, @items) {
		print 'Searched IDs information : '. Dumper \@items;
	} else {
		print "Please, check ur $arrayName input values.\n";
		print "(There are elements in the $arrayName, but they are either empty strings, or strings with spaces..)\n";
	  exit 1;
	}

	return \@items;
}

sub getArrayItemsByUnderBar {
    my $arrayName = shift;
    my $strArray = shift; 

    $strArray =~ s/^\s+|\s+$//g;

    my @items = grep { $_ ne '' } split(/_/, $strArray);

    if (! @items) { 
        print "Please, check ur $arrayName input values.\n";
        print "(There is no elements in the $arrayName..)\n";
        exit 1;
    }elsif (grep ! /^\s*$/, @items) {
        print 'Searched IDs information : '. Dumper \@items;
    } else {
        print "Please, check ur $arrayName input values.\n";
        print "(There are elements in the $arrayName, but they are either empty strings, or strings with spaces..)\n";
     exit 1;
    }

    return \@items;
}

property.description:
sub getDirSize {
  my $dir = shift;
  my $size = 0;

  opendir(D,"$dir") || return 0;
  foreach my $dirContent (grep(!/^\.\.?/,readdir(D))) {
     my $st=stat("$dir/$dirContent");
     if (S_ISREG($st->mode)) {
       $size += $st->size;
     } elsif (S_ISDIR($st->mode)) {
       $size += getDirSize("$dir/$dirContent");
     }
  }
  closedir(D);
  return $size;
}

property.value:
sub getDirSize {
  my $dir = shift;
  my $size = 0;

  opendir(my $dh,"$dir") || return 0;
  foreach my $dirContent (grep(!/^\.\.?/,readdir($dh))) {
     my $st=stat("$dir/$dirContent");
     if (S_ISREG($st->mode)) {
       $size += $st->size;
     } elsif (S_ISDIR($st->mode)) {
       $size += getDirSize("$dir/$dirContent");
     }
  }
  closedir($dh);
  return $size;
}

sub getFileParseInfo {
     my $filePath = shift;
	 my ($fileNameOnly, $dir, $ext) = fileparse($filePath, '\.[^.]*');
     $dir =~ s/\\/\//g;
     $dir =~ s/\/\//\//g;
     $ext =~ s/\.//g;

	 my $size = getFileSize($filePath);

	 return ($fileNameOnly, $dir, $ext, $size);
}

