sub getNetworkDriveStatus {
	my %netuseDrives;
	my @wmic_output=`WMIC NETUSE LIST FULL /FORMAT:CSV`;
	my @title_data;
	my $LocalNameIndex;

	foreach my $wmic_line (@wmic_output)
	{
		chomp $wmic_line;
		$wmic_line=~s/\r$//;
		next if $wmic_line eq "";
		if(0==scalar @title_data)
		{
			@title_data=split /,/,$wmic_line;
			for(my $idx=0;$idx<scalar @title_data;$idx++)
			{
				$LocalNameIndex=$idx if $title_data[$idx]=~/^LocalName$/i;
			}
			die "no LocalName found in title" if !defined $LocalNameIndex;
		}
		else
		{
			my @netuseEntry=split /,/,$wmic_line;
			for(my $idx=0;$idx<scalar @title_data;$idx++)
			{
				$netuseDrives{uc $netuseEntry[$LocalNameIndex]}{$title_data[$idx]}=$netuseEntry[$idx];
			}
		}
	}

	return \%netuseDrives;
}

sub getOptionXMLTextFromHash{
    my $optionListHashRef = shift;

    my %optionList = ();
    my @optionArr = ();
    my $optionCount = 0;

    foreach my $optionName  ( sort keys %{$optionListHashRef}){
        my @oneOption = {'name'=>"$optionName", 'value'=>"$optionListHashRef->{$optionName}"}; 
        push( @optionArr, @oneOption);
        $optionCount ++;
    }

    $optionList{"option"} = \@optionArr;

    my $xs = new XML::Simple;
    my $xml = $xs->XMLout(\%optionList,
                       NoAttr => 1,
                       RootName => undef
                     );

    return $xml;
}

sub getTextAreaElemsByNewline {
	my $text = shift;
	$text =~ s/^\s+|\s+$//g;

	my @fields = grep { $_ ne '' } split(/\n/, $text);

	my @filterdFields = ();
	foreach(@fields) {
		$_ =~ s/^\s+|\s+$//g;
		push(@filterdFields, $_);
	}

	#print 'Result : ' . Dumper \@filterdFields;

	return \@filterdFields;
}

sub humanSize {
  my $size = shift();

  if ($size > 1099511627776) { # TB: 1024 GB
      return sprintf("%.2f TB", $size / 1099511627776);
  }
  if ($size > 1073741824) { # GB: 1024 MB
      return sprintf("%.2f GB", $size / 1073741824);
  }
  if ($size > 1048576) { # MB: 1024 KB
      return sprintf("%.2f MB", $size / 1048576);
  }
  elsif ($size > 1024) { # KiB: 1024 B
      return sprintf("%.2f KB", $size / 1024);
  }
                                  # bytes
  return "$size byte" . ($size <= 1 ? "" : "s");
}

sub listFiles{
    my ($target, $filter) = @_;

    my $list = "";
	my $pathSeparator = "";

    ($^O eq "linux") ? ($pathSeparator = "/"): ($pathSeparator = "\\");
	if(!($target =~ m/\/$|\\$/)){
       $target .= $pathSeparator if ($target);
    }

	if(!defined($filter) || $filter eq ""){
        $filter = "*.*";
    }

    my @files = bsd_glob($target.$filter);

    return \@files;
}

sub openCfgFile {
    my ($cfg) = shift;            # the file parameter
    # open and read
    open CFG, "<", $cfg or die  "File not found: $!";  
        my @settings = <CFG>;
    close CFG;

    my %values;                  
    foreach (@settings) {       
        chomp;
        s|#.+||;
        #s|"(.+?)"|$1|g;
        #s|\s||;
        my( $key, $val) = split /=/;
        next unless(defined $key);
        next unless(defined $val);
        $values{$key}=$val;  
    }
    return %values;
}

sub renameFile{
    my ($oldName, $newName) = @_;
    if(!checkExistence($oldName)){
        print "File \"$oldName\" doesn't exist.\n";
        return 0;
    }

    if(checkExistence($newName)){
        print "Unable to rename \"$oldName\", file \"$newName\" already exists.\n";
        return 0;
    }

    my ($volume,$directories,$file) = File::Spec->splitpath( $newName );

    #if user does not give us an absolute path we need to create it according to the old name path
    if($volume eq "" || $directories eq ""){
        ($volume,$directories) = File::Spec->splitpath( $oldName );
        $newName = $volume . $directories . $file;
    }

    if(rename($oldName,$newName) == 0){
        print "$!";
		return 0
    }

	return 1;
}

