	if (!$success) {
		printf(" Error deleteWorkflowLink - %s\n", $propertyPath);
		printf(" %s: %s\n", $errCode, $errMsg);
		return 0;
	} 

	return 1;
}

sub getProjectWorkflowDefNames {
    my $ec = shift;
    my $projectName = shift;

    my ($success, $xPath, $errMsg, $errCode)= invokeCommand($ec, "SuppressLog IgnoreError", "getWorkflowDefinitions",{'projectName' => "$projectName"});

    if (! $success) {
        printf(" Error getProjectWorkflows - %s", $projectName);
        printf(" %s: %s\n", $errCode, $errMsg);
        return undef;
    }

    my @workflowDefinitionNames = ();

    my $nodeset = $xPath->find('//workflowDefinition');

    foreach my $node ($nodeset->get_nodelist) {
        my $workflowDefinitionName = $xPath->findvalue('workflowDefinitionName', $node);
        push @workflowDefinitionNames, "$workflowDefinitionName";
    }

    return \@workflowDefinitionNames;
}

