sub getWorkflowInfoRecursively {
	my ($ec, $project, $workflow) = @_ ;

	my %exclude = (
		"actualParameters" , 1,
		"combinedStatus" , 1,
		"createTime" , 1,
		"description" , 1,
		"lastModifiedBy" , 1,
		"modifyTime" , 1,
		"owner" , 1,
		"propertySheetId" , 1,
		"workspace" , 1,
		"workflowName", 1,
		"trigger", 1,
	);

	my %workflowData;

	my $result = $ec->getWorkflow($project , $workflow);
	foreach my $workflowElement ($result->findnodes("/responses/response/workflow/* ")) {
		my $name = $workflowElement->getName();
		my $value = $workflowElement->string_value();
		if (! defined ( $exclude { $name }) && $value ne " " ) {
			$workflowData { $name } = $value ;
		}
	}

	my $compositeJobOutcome = "success" ;

	# Save information for each of the workflow's states.
	my @statesData = ();
	my $stateIndex = 0;

	$result = $ec->getStates( $project , $workflow );
	foreach my $state($result->findnodes("/responses/response/state")) {

		my %stateData;
		my $stateName = $state->findvalue("stateName")->string_value();

		foreach my $stateElement ($state->findnodes("*")) {
			my $name = $stateElement->getName();
			my $value = $stateElement->string_value();
			if (!defined($exclude{$name}) && $value ne "" ) {
				$stateData{$name} = $value;
				my $subprocessOutcome = "";

				if ($name eq "subworkflow") {
					 if ( $value =~ m|/projects/(.*)/workflows/(.*)|) {
						$stateData{"subworkflowDetail"} = getWorkflowInfoRecursively($ec, $1,$2);
					} else {
						$stateData{"subworkflowDetail"} = getWorkflowInfoRecursively($ec, $project,$value);
					}

					$subprocessOutcome = $stateData{"subworkflowDetail"}{"compositeJobOutcome"};

				} elsif ( $name eq "subjob" ) {
					my %jobData;
					$jobData{'jobId'} = $value; 
					my $jobInfo = $ec->getJobInfo($value);
					foreach my $jobElement ( $jobInfo -> findnodes( " /responses/response/job/* " )) {
						my $name = $jobElement->getName();
						my $value = $jobElement->string_value();
						if (!defined($exclude{$name}) && $value ne "") {
							$jobData{$name} = $value;
						}
					}

					$stateData{"subjobDetail"} = \%jobData;
					$subprocessOutcome = $jobInfo->findvalue("//outcome")->string_value();
					$jobData{"outcome"} =  $subprocessOutcome; 
				}

				if($subprocessOutcome ne "" && $compositeJobOutcome ne "error" ) {
					if ($compositeJobOutcome eq "success" && $subprocessOutcome ne "success" ) {
						$compositeJobOutcome = $subprocessOutcome ;
					} elsif ( $compositeJobOutcome eq "warning" && $subprocessOutcome eq "error" ) {
						$compositeJobOutcome = $subprocessOutcome ;
					}
				}
			}
		}

		# Save information for each of the state's transitions.
		my @transitionsData = ();
		my $transitionIndex = 0;

		$result = $ec->getTransitions($project , $workflow , $stateName );
		foreach my $transition($result->findnodes("/responses/response/transition")) {
			my %transitionData;
			my $transitionName = $transition->findvalue("transitionName")->string_value();

			foreach my $transitionElement($transition->findnodes("*")) {
				my $name = $transitionElement->getName();
				my $value = $transitionElement->string_value();
				if (!defined ($exclude{$name}) && $value ne "" ) {
					$transitionData{$name} = $value;
				}
			}
			$transitionsData[$transitionIndex] = \%transitionData ;
			$transitionIndex ++;
		}

		$stateData{'transition'} = \@transitionsData ;
		$statesData[$stateIndex] = \%stateData ;
		$stateIndex ++;
	}

	$workflowData{'compositeJobOutcome'} = $compositeJobOutcome;
	$workflowData{'state'} = \ @statesData;

	return \%workflowData;
}

sub getWorkflowNames {
    my $ec = shift;
    my $projectName = shift; #user or group 

    my ($success, $res, $errMsg, $errCode)= invokeCommand($ec, "SuppressLog IgnoreError", "getWorkflows", { 'projectName' => $projectName}); 
    if (! $success) {
        print("  Error getting workflows => ProjectName : $projectName");
        print(" $errCode: $errMsg\n");
        return undef ;
    }

    #print $res ->findnodes_as_string('/') . "\n";


    my $nodeset = $res->findnodes("//workflow");    

    my @workflowNames;
    foreach ($nodeset->get_nodelist) {

        push @workflowNames, $_->findvalue("workflowName");
    }

    return \@workflowNames;
}

sub getWorkflowState {
	my ($ec, $projectName, $workflowName) = @_;

	my $workflowInfo = getWorkflowInfoRecursively($ec, $projectName, $workflowName);

	print "Workflow Information:". Dumper $workflowInfo;

	if($workflowInfo->{'completed'} == 1 ) {
		return 'completed';
	}
	if($workflowInfo->{'compositeJobOutcome'} eq 'error' ) { 
		return 'error';
	}

	my @subJobs = ();
	my @subJobNames = ();
	my $abortStateName = '';
	my $abortedWorkflow = 0;
	my $errorStatingState = 0;
	my $startingStateName = $workflowInfo->{'startingState'};

	#Check Job Abort And Get Whole Jobs
	foreach (@{ $workflowInfo->{'state'} }) {
		my $stateName = $_->{'stateName'};
		my $subJobName = $_->{'subjob'};
		unless(defined $subJobName && $subJobName ne '') {
			next;
		}

		push(@subJobs,$_->{"subjobDetail"}{'jobId'});
		push(@subJobNames, $subJobName);

		my $subAbortState = $_->{'subjobDetail'}{'abortStatus'};
		unless(defined $subAbortState && $subAbortState eq 'ABORT') {
			next;
		}

		$abortStateName = $stateName;
		$abortedWorkflow = 1;
	}

	#Check Stating State Error
	unless($abortedWorkflow) {
		foreach (@{ $workflowInfo->{'state'} }) {
			my $stateName = $_->{'stateName'};
			if($stateName ne $startingStateName) {
				next;
			} 

			my $subJobName = $_->{'subjob'};
			unless(defined $subJobName && $subJobName ne '') {
				next;
			}

			my $subJobOutCome = $_->{'subjobDetail'}{'outcome'};
			if($subJobOutCome ne 'error'){
				next;
			}

			$errorStatingState = 1;
		}
	}

	if($abortedWorkflow){
		print "Workflow State :   Abort ( $abortStateName state )\n";
		return 'abort';
	} elsif($errorStatingState) {  
		print "Workflow State :   Error ( $startingStateName state) \n";
		return 'error';
	} else {
		print "Workflow State :   Succeed\n"; 
		return 'complete';
	}
}

sub runWorkflow {
    my ($ec, $projectName, $workflowDefinitionName, $actualParameters, $autoURL) = @_;

    my $success = undef;
    my $xPath = undef;
    my $errMsg = undef;
    my $errCode = undef;

    if(defined $actualParameters) {
        my @actualParameterAOH = ();

        foreach my $actualParameterName ( sort keys %{$actualParameters} )
        {
            my $actualParameterValue = $actualParameters->{$actualParameterName};
            push @actualParameterAOH, { actualParameterName => "$actualParameterName", value => "$actualParameterValue"};
        }

        ($success, $xPath, $errMsg, $errCode)= invokeCommand($ec, "SuppressLog IgnoreError", "runWorkflow", { 'projectName' => "$projectName", 'workflowDefinitionName' => "$workflowDefinitionName", 'actualParameter' => \@actualParameterAOH });
        if (! $success) {
            printf(" Error runWorkflow - projects : %s , workflowDefinitionName : %s", $projectName, $workflowDefinitionName);
            printf(" %s: %s\n", $errCode, $errMsg);
            return undef;
        }
    } else {
        ($success, $xPath, $errMsg, $errCode)= invokeCommand($ec, "SuppressLog IgnoreError", "runWorkflow", { 'projectName' => "$projectName", 'workflowDefinitionName' => "$workflowDefinitionName"});
        if (! $success) {
            printf(" Error runWorkflow - projects : %s , workflowDefinitionName : %s", $projectName, $workflowDefinitionName);
            printf(" %s: %s\n", $errCode, $errMsg);
            return undef;
        }    
    }

    #print $xPath ->findnodes_as_string('/') . "\n";
     my $workflowName = $xPath->findvalue("//workflowName")->value();

    do{
        sleep(1);
    }while (checkWorkflowExist($ec, $projectName, $workflowName) == 0);

    if(defined $autoURL) {
        my $link = "link/workflowDetails/projects/$projectName/workflows/$workflowName" ;
        $ec->setProperty("/myJob/report-urls/Workflow : $workflowName" , $link ); 
    }

    return "$workflowName";
}

sub transitionWorkflow {
  my ($ec, $projectName, $workflowName, $stateName, $transitionName, $actualParameters) = @_;

  my ($success, $xPath, $errMsg, $errCode)= invokeCommand($ec, "SuppressLog IgnoreError", "getState",
                                                     {'projectName' => "$projectName", 
                                                     'workflowName' => "$workflowName", 
                                                     'stateName' => "$stateName"});

  if (! $success) {
    printf(" Error - transitionWorkflow => Invalid State Name - project : %s, workflow : %s, state : %s \n", $projectName, $workflowName, $stateName);
    printf(" %s: %s\n", $errCode, $errMsg);
    return 0;
  } 

  my %params = ();
  $params{'projectName'} = "$projectName";
  $params{'workflowName'} = "$workflowName";
  $params{'stateName'} = "$stateName";
  $params{'transitionName'} = "$transitionName";

  my @actualParameterAOH = ();
  if(defined $actualParameters) {
		foreach my $actualParameterName ( sort keys %{$actualParameters} )
		{
			my $actualParameterValue = $actualParameters->{$actualParameterName};
			push @actualParameterAOH, { 'actualParameterName' => "$actualParameterName", 'value' => "$actualParameterValue"};
		}

		my $count = @actualParameterAOH;
		if( $count > 0 ) {
			$params{'actualParameter'} = \@actualParameterAOH;
		}
   }

   ($success, $xPath, $errMsg, $errCode)= invokeCommand($ec, "SuppressLog IgnoreError", "transitionWorkflow",\%params); 

   if (! $success) {
		printf(" Error - transitionWorkflow - project : %s, workflow : %s, state : %s transitionWorkflow : %s \n", $projectName, $workflowName, $stateName, $transitionName);
		printf(" %s: %s\n", $errCode, $errMsg);
		return 0;
	}

	return 1;
}

