def pipe = $[pipe] def projName = "$[projName]"
project projName, {
property "normalReleaseMode" // Used to flag whether tasks are run or not; use 0 to run seeding release pipeline pipe.name+" - $[issueKey]", { pipe.stages.eachWithIndex { st, index -> stage st,{ task "Batch Deploy", taskType: "DEPLOYER", errorHandling: "stopOnError"
if (index == 0) { task "Update Ticket For Deploy - $[issueKey]", taskType: 'PROCEDURE', subproject: projName, subprocedure: "Update Ticket For Deploy - $[issueKey]" task "Manual Validation", taskType: "MANUAL", approvers:['admin'], instruction: "Verify that business requirements are met" }
if (index == 1) { task "System Tests For QA", taskType: 'PROCEDURE', subproject: projName, subprocedure: "System Tests", errorHandling: "stopOnError" task "Manual Validation", taskType: "MANUAL", approvers:['admin'], instruction: "Verify that business requirements are met" } if (index == 2) { task "Update Ticket For Finish - $[issueKey]", taskType: 'PROCEDURE', subproject: projName, subprocedure: "Update Ticket For Finish - $[issueKey]" task "Manual Validation", taskType: "MANUAL", approvers:['admin'], instruction: "Verify that business requirements are met" } if (index > 0) { task "Entry gate approval", // Don't create a gate for first stage taskType: 'APPROVAL', approver: ['admin'], gateType: 'PRE', notificationTemplate: 'ec_default_pipeline_notification_template' } } // stage } // Each stage } // Pipeline } // Project

