step.description:
import com.electriccloud.client.groovy.ElectricFlow
import com.electriccloud.client.groovy.models.ActualParameter
import com.electriccloud.client.groovy.models.Credential

ElectricFlow ef = new ElectricFlow()

ef.setProperty(propertyName: "/myPipelineRuntime/issueKey",value: "")

def sout = new StringBuilder(), serr = new StringBuilder()
def proc ='git log --pretty=format:%h'.execute() | 'cut -c 1-8'.execute() | 'head -n 1'.execute()
proc.consumeProcessOutput(sout, serr)
proc.waitForOrKill(1000)
println "out> $sout err> $serr"

String trimmedSout = "$sout".trim();
def result = ef.setProperty(propertyName: "/myPipelineRuntime/tagName",value: "$trimmedSout")
println(result)

