$[/myProject/scripts/credFuncs/loginByCred]


my $ec = new ElectricCommander();
loginByCred($ec,"efLogin");
 my $issueKey = getPropertyStringValue($ec, "/myjob/getIssuesResult/issueKeys");

 my %parameters = (
    GenerateRelease  => "true",
    IssueKey => "$issueKey" 
);

my $counter = 50; 
my $workflowName = CHECK_EF_FUNC_ERROR( $ec, 'runWorkflow', '$[/myProject/projectName]', 'Build-Test-Package', \%parameters, 1);
while($counter > 0){
	my $activeStateName = CHECK_EF_FUNC_ERROR( $ec, 'getWorkflowActiveStateName', '$[/myProject/projectName]', "$workflowName");

	print "Active State Name : $activeStateName \n";
	if("$activeStateName" eq "Failed") {
		print "Failed";
		exit 1;
	}

	if("$activeStateName" eq "End") {
		print "Succeed";
        exit 0;
	}

	sleep(5);
	$counter--;
}

exit 1;

