import com.electriccloud.client.groovy.ElectricFlow
import com.electriccloud.client.groovy.models.*


ElectricFlow ef = new ElectricFlow()

if (ef.getProperty(propertyName: "/myPipelineRuntime/BRANCH")) {
        ef.setProperty(propertyName : "BRANCH", 
                      value : "$[/myPipelineRuntime/BRANCH]",
              flowRuntimeId : "$[/myPipelineStageRuntime/flowRuntimeID]")
}

if (ef.getProperty(propertyName: "/myPipelineRuntime/GitTagName")) {
