step.command:
def projName  = "$[/myProject/projectName]"
def envs = ["dev","qa","prod"]
def resources = []
def environments = []
def helmValues = ''

envs.each { env ->

    def appName = (String)"${projName}_${env}"
    def envName = (String)"${env}"
    def isDev = envName.toLowerCase().contains("dev")
    def isQA = envName.toLowerCase().contains("qa")

    if(isDev) {
      helmValues =  (String)"""
$[devValues]
"""
    } else if (isQA) {  
      helmValues =  (String)"""
$[qaValues]
"""
    } else {
      helmValues = (String)"""
$[prodValues]
"""
    }

    application appName, { 
    applicationType = 'microservice'
    projectName = projName

    microservice appName, {
        applicationName = appName

        definitionSource = 'helm_repository'

        definitionSourceParameter = [
        'repository_credential': '/projects/Guestbook/credentials/harbor_cred',
        'repositoryName': 'guestbook-repo',
        'repositoryUrl': 'https://harbor.idtplateer.com/chartrepo/guestbook',
        ]

        definitionType = 'helm'

        deployParameter = [
        'additionalOptions': '--create-namespace',
        'chart': 'guestbook',
        'releaseName': 'my-guestbook',
        'version': '0.1.0',
        'values': helmValues,
        ]

        rollbackParameter = [
        'rollbackEnabled': 'true',
        'waitTimeout': '300',
        ]

        process 'Deploy Microservice Process', {
        description = 'System generated process for microservice deployment'
        microserviceName = appName
        processType = 'DEPLOY'
        processStep 'Retrieve Artifact', {
            description = 'System generated step to retrieve microservice definition artifact'
            processStepType = 'plugin'
            subprocedure = 'Source Provider'
            subproject = '/plugins/EC-Helm/project'
        }

        processStep 'Deploy Microservice', {
            description = 'System generated step to deploy microservice'
            processStepType = 'plugin'
            subprocedure = 'Deploy Service'
            subproject = '/plugins/EC-Helm/project'
        }

        processDependency 'Retrieve Artifact', targetProcessStepName: 'Deploy Microservice'
        }
    }

    process 'Deploy Application', {
        description = 'System generated process for microservice application'
        applicationName = appName
        processType = 'DEPLOY'
        formalParameter 'ec_Guestbook-run', defaultValue: '1', {
        expansionDeferred = '1'
        type = 'checkbox'
        }

        processStep 'Guestbook', {
        description = 'System generated step to invoke microservice process'
        processStepType = 'process'
        submicroservice = appName
        submicroserviceProcess = 'Deploy Microservice Process'
        useUtilityResource = '1'

        property 'ec_deploy', {
            ec_notifierStatus = '0'
        }
        }

        property 'ec_deploy', {
        ec_notifierStatus = '0'
        }
    }

    process 'Uninstall Application', {
        applicationName = appName
        exclusiveEnvironment = '0'
        processType = 'UNDEPLOY'
        workspaceName = 'default'

        processStep 'Delete Release', {
        actualParameter = [
            'actionOnError': '0',
            'config': '/projects/Guestbook/pluginConfigurations/helmcfg',
            'errorValue': '',
            'options': (String)"-n ${envName}",
            'releaseName': 'my-guestbook',
            'resultPropertySheet': '/myJob/deleteRelease',
        ]
        dependencyJoinType = 'and'
        processStepType = 'plugin'
        subprocedure = 'Delete Release'
        subproject = '/plugins/EC-Helm/project'

        // Custom properties

        property 'ec_deploy', {

            // Custom properties
            ec_notifierStatus = '0'
        }
        }

        processStep 'Delete PVC', {
        actualParameter = [
            'commandToRun': (String)"""
            kubectl delete pvc -l app.kubernetes.io/instance=my-guestbook -n ${envName}
            kubectl delete ns ${envName}
            """,
        ]
        dependencyJoinType = 'and'
        processStepType = 'command'
        subprocedure = 'RunCommand'
        subproject = '/plugins/EC-Core/project'
        useUtilityResource = '1'
        utilityResourceName = 'local'
        property 'ec_deploy', {
            ec_notifierStatus = '0'
        }
        }

        processDependency 'Delete Release', targetProcessStepName: 'Delete PVC'

        property 'ec_deploy', {
        ec_notifierStatus = '0'
        }
    }

    property 'ec_deploy', { 
        ec_notifierStatus = '0'
    } 
    }

}

