step.command:
def projName  = "$[/myProject/projectName]"
def envs = ["dev","qa","prod"]
def resources = []
def environments = []

envs.each { env ->
def envName = (String)"${projName}_${env}"
def ns = (String)"${env}"

environment envName, { 
  projectName = projName

  cluster envName, {
    definitionParameter = [
      'config': '/projects/Guestbook/pluginConfigurations/helmcfg',
      'kubecontext': '',
      'namespace': ns,
    ]
    environmentName = envName
    pluginKey = 'EC-Helm'
  }

  utilityResource 'local', {
    resourceName = 'local'
  }
}

}

step.description:
def projName  = "$[/myProject/projectName]"
def envs = ["guestbook-dev","guestbook-qa","guestbook-prod"]
def resources = []
def environments = []

envs.each { env ->
environment env, { 
  projectName = projName

  cluster env, {
    definitionParameter = [
      'config': '/projects/Guestbook/pluginConfigurations/helmcfg',
      'kubecontext': '',
      'namespace': 'plateer',
    ]
    environmentName = env
    pluginKey = 'EC-Helm'
  }

  utilityResource 'local', {
    resourceName = 'local'
  }
}
}

step.command:
def projName = "$[/myProject/projectName]"
def envs = ["dev","qa","prod"]
def resources = []
def environments = []
def helmValues = ''
def appName = ''
def envName = ''
def isQA = false
def isDev = false

envs.each { env ->

    appName = (String)"${projName}_${env}"
    envName = (String)"${env}"
    isDev = envName.toLowerCase().contains("dev")
    isQA = envName.toLowerCase().contains("qa")

    if(isDev) {
      helmValues = (String)"""
$[devValues]
      """
    } else if (isQA) {
      helmValues = (String)"""
$[qaValues]
"""
    } else {
      helmValues = (String)"""
$[prodValues]
"""
    }

    application appName, {
        applicationType = 'microservice'
        projectName = projName

        microservice appName, {
            applicationName = appName

            definitionSource = 'helm_repository'

            definitionSourceParameter = [
                'repository_credential': '/projects/Guestbook/credentials/harbor_cred',
                'repositoryName': 'guestbook-repo',
                'repositoryUrl': 'https://harbor.idtplateer.com/chartrepo/guestbook',
            ]

            definitionType = 'helm'

            deployParameter = [
                'additionalOptions': '--create-namespace',
                'chart': 'guestbook',
                'releaseName': 'my-guestbook',
                'version': '0.1.0',
                'values': helmValues,
            ]

            rollbackParameter = [
                'rollbackEnabled': 'true',
                'waitTimeout': '300',
            ]
        }

        process 'Deploy Application', {
            description = 'System generated process for microservice application'
            applicationName = appName
            processType = 'DEPLOY'
            formalParameter 'ec_Guestbook-run', defaultValue: '1', {
                expansionDeferred = '1'
                type = 'checkbox'
            }

            property 'ec_deploy', {
                ec_notifierStatus = '0'
            }
        }

        process 'Uninstall Application', {
            applicationName = appName
            exclusiveEnvironment = '0'
            processType = 'UNDEPLOY'
            workspaceName = 'default'

            processStep 'Delete Release', {
                actualParameter = [
                    'actionOnError': '0',
                    'config': '/projects/Guestbook/pluginConfigurations/helmcfg',
                    'errorValue': '',
                    'options': (String)"-n ${envName}",
                    'releaseName': 'my-guestbook',
                    'resultPropertySheet': '/myJob/deleteRelease',
                ]
                dependencyJoinType = 'and'
                processStepType = 'plugin'
                subprocedure = 'Delete Release'
                subproject = '/plugins/EC-Helm/project'

                property 'ec_deploy', {
                    ec_notifierStatus = '0'
                }
            }

            processStep 'Delete PVC', {
                actualParameter = [
                    'commandToRun': (String)"""
                    kubectl delete pvc -l app.kubernetes.io/instance=my-guestbook -n ${envName}
                    kubectl delete ns ${envName}
                    """,
                ]
                dependencyJoinType = 'and'
                processStepType = 'command'
                subprocedure = 'RunCommand'
                subproject = '/plugins/EC-Core/project'
                useUtilityResource = '1'
                utilityResourceName = 'local'
                property 'ec_deploy', {
                    ec_notifierStatus = '0'
                }
            }

            createProcessDependency componentApplicationName: appName,
                processStepName: 'Delete Release',
                targetProcessStepName: 'Delete PVC'

            property 'ec_deploy', {
                ec_notifierStatus = '0'
            }
        }

        process 'Junk Process', {
          applicationName = appName
          exclusiveEnvironment = '0'
          processType = 'OTHER'
          workspaceName = 'default'

          processStep 'Junk', {
            actualParameter = [
              'commandToRun': (String)"""
              echo "nothing"
              """,
            ]
            dependencyJoinType = 'and'
            processStepType = 'command'
            subprocedure = 'RunCommand'
            subproject = '/plugins/EC-Core/project'
            useUtilityResource = '1'
            utilityResourceName = 'local'
            property 'ec_deploy', {
              ec_notifierStatus = '0'
            }
          }
          property 'ec_deploy', {
           ec_notifierStatus = '0'
          }
        }        

        if(isQA) {
          process 'Upgrade Application', {
            applicationName = appName
            exclusiveEnvironment = '0'
            processType = 'OTHER'
            workspaceName = 'default'

            processStep 'Upgrade Release', {
              actualParameter = [
                'commandToRun': (String)"""
                kubectl exec -n ${envName} -it my-guestbook-redis-master-0  --  redis-cli -h localhost save
                kubectl scale statefulsets -l app.kubernetes.io/instance=my-guestbook --replicas=0 -n ${envName}
                helm upgrade  my-guestbook guestbook-repo/guestbook -n ${envName} --wait
                """,
              ]
              dependencyJoinType = 'and'
              processStepType = 'command'
              subprocedure = 'RunCommand'
              subproject = '/plugins/EC-Core/project'
              useUtilityResource = '1'
              utilityResourceName = 'local'
              property 'ec_deploy', {
                ec_notifierStatus = '0'
              }
            }
            property 'ec_deploy', {
              ec_notifierStatus = '0'
            }
          }

          process 'Rollback Application', {
            applicationName = appName
            exclusiveEnvironment = '0'
            processType = 'OTHER'
            workspaceName = 'default'

            processStep 'Rollback App', {
              actualParameter = [
                'commandToRun': (String)"""
                helm history my-guestbook -n ${envName}
                kubectl scale statefulsets -l app.kubernetes.io/instance=my-guestbook --replicas=0 -n ${envName}
                sleep 5
                helm rollback my-guestbook 1 -n ${envName} --wait
                helm history my-guestbook -n ${envName}
                """,
              ]
              dependencyJoinType = 'and'
              processStepType = 'command'
              subprocedure = 'RunCommand'
              subproject = '/plugins/EC-Core/project'
              useUtilityResource = '1'
              workspaceName = 'default'

              property 'ec_deploy', {
                ec_notifierStatus = '0'
              }
            }
            property 'ec_deploy', {
              ec_notifierStatus = '0'
            }
          }

          process 'Deploy App With Rollback', {
            applicationName = appName
            exclusiveEnvironment = '0'
            processType = 'OTHER'
            formalParameter 'ec_Guestbook_qa-run', defaultValue: '1', {
              expansionDeferred = '1'
              type = 'checkbox'
            }
            processStep 'Deploy App', {
              dependencyJoinType = 'and'
              processStepType = 'process'
              submicroservice = 'Guestbook_qa'
              submicroserviceProcess = 'Deploy Microservice Process'
              useUtilityResource = '1'
              utilityResourceName = 'local'
              workspaceName = 'default'
              property 'ec_deploy', {
                ec_notifierStatus = '0'
              }
            }
            processStep 'Manual Test', {
              dependencyJoinType = 'and'
              notificationEnabled = '1'
              notificationTemplate = 'ec_default_manual_process_step_notification_template'
              processStepType = 'manual'
              assignee = [
                'admin',
              ]
              property 'ec_deploy', {
                ec_notifierStatus = '1'
              }
            }
            processStep 'App DB Snapshot', {
              dependencyJoinType = 'and'
              processStepType = 'procedure'
              subprocedure = 'UpgradeRelease'
              subproject = 'Guestbook'
              useUtilityResource = '1'
              workspaceName = 'default'
              property 'ec_deploy', {
                ec_notifierStatus = '0'
              }
            }
            processStep 'Add more data', {
              dependencyJoinType = 'and'
              errorHandling = 'failProcedure'
              notificationEnabled = '1'
              notificationTemplate = 'ec_default_manual_process_step_notification_template'
              processStepType = 'manual'
              workspaceName = 'default'
              assignee = [
                'admin',
              ]
              property 'ec_deploy', {
                ec_notifierStatus = '1'
              }
            }
            processStep 'Rollback App', {
              componentRollback = '0'
              dependencyJoinType = 'and'
              processStepType = 'rollback'
              rollbackType = 'environment'
              rollbackUndeployProcess = 'Rollback Application'
              skipRollbackIfUndeployFails = '0'
              smartRollback = '0'
              workspaceName = 'default'
              property 'ec_deploy', {
                ec_notifierStatus = '0'
              }
            }

            processStep 'Continue without rollback', {
              actualParameter = [
                'commandToRun': 'echo "continue"',
              ]
              dependencyJoinType = 'and'
              processStepType = 'command'
              subprocedure = 'RunCommand'
              subproject = '/plugins/EC-Core/project'
              useUtilityResource = '1'
              property 'ec_deploy', {
                ec_notifierStatus = '0'
              }
            }

            processDependency 'App DB Snapshot', targetProcessStepName: 'Add more data'
            processDependency 'Add more data', targetProcessStepName: 'Rollback App', {
              branchType = 'SUCCESS'
            }
            processDependency 'Add more data', targetProcessStepName: 'Continue without rollback', {
              branchType = 'ERROR'
            }
            processDependency 'Deploy App', targetProcessStepName: 'Manual Test'
            processDependency 'Manual Test', targetProcessStepName: 'App DB Snapshot'
            property 'ec_deploy', {
              ec_notifierStatus = '1'
            }
          }
        }

        property 'ec_deploy', {
            ec_notifierStatus = '0'
        }
    }
}

