step.command:
def projName = "$[/myProject/projectName]" def envs = ["dev","qa","prod"] def resources = [] def environments = []
envs.each { env -> def appName = (String)"${projName}_${env}" def tierMapName = (String)"${projName}_${env}_map" def envName = (String)"${projName}_${env}"
application appName, { applicationType = 'microservice' projectName = projName
tierMap tierMapName, { applicationName = appName environmentName = envName environmentProjectName = projName microserviceMapping tierMapName, { clusterName = envName clusterProjectName = projName microserviceName = appName tierMapName = tierMapName } } }
}

step.description:
def projName  = "$[/myProject/projectName]"
def envs = ["guestbook-dev","guestbook-qa","guestbook-prod"]
def resources = []
def environments = []

envs.each { env ->
environment env, { 
  projectName = projName

  cluster env, {
    definitionParameter = [
      'config': '/projects/Guestbook/pluginConfigurations/helmcfg',
      'kubecontext': '',
      'namespace': 'plateer',
    ]
    environmentName = env
    pluginKey = 'EC-Helm'
  }

  utilityResource 'local', {
    resourceName = 'local'
  }
}
}

step.command:
def projName  = "$[/myProject/projectName]"
def pipeName = projName + "_CI_Pipeline"

pipeline pipeName, {
  projectName = projName

  formalParameter 'ec_stagesToRun', {
    expansionDeferred = '1'
  }

  stage 'CI', {
    colorCode = '#289ce1'
    pipelineName = pipeName
    gate 'PRE', {
    }

    gate 'POST', {
    }

    task 'Guestbook CI', {
      actualParameter = [
        'InstallPlugin': 'false',
      ]
      ciConfigurationName = 'jenkinscfg'
      ciJobFolder = '/'
      ciJobName = 'guestbook'
      subproject = projName
      taskType = 'CI_JOB'
    }

    task 'Hashicup CI', {
      actualParameter = [
        'InstallPlugin': 'false',
      ]
      ciConfigurationName = 'jenkinscfg'
      ciJobFolder = '/'
      ciJobName = 'hashicup'
      errorHandling = 'continueOnError'
      subproject = projName
      taskType = 'CI_JOB'
    }	

    task 'Collect CI Result', {
      subprocedure = 'CollectCIResult'
      subproject = projName
      taskType = 'PROCEDURE'
    }    
  }
}

def projName  = "$[/myProject/projectName]"
def pipeName = "$[/myProject/projectName]_CD_Pipeline"
def envs = ["dev","qa","prod"]

pipeline pipeName, {
  projectName = projName

  formalParameter 'ec_stagesToRun', {
    expansionDeferred = '1'
  }

  envs.each { env ->

    def envName = (String)"${projName}_${env}"
    def isDev = env.toLowerCase().contains("dev")
    def isQA = env.toLowerCase().contains("qa")
    def isProd = env.toLowerCase().contains("prod")
    def stageColor = ''
    def stageName = ''
    def deployAppDispName = ''
    def collectQAResultDispName = ''
    def undeployAppDispName = ''
    def subprocessName = ''

    if(isDev) {
        stageColor = '#ff7f0e'
        stageName = "Dev"
        deployAppDispName = "Deploy Dev App"
        collectQAResultDispName = "Collect Dev Result"
        undeployAppDispName = "Undeploy Dev App"
        collectProcedureName = "CollectDevResult"
        subprocessName = 'Deploy Application'
    } else if (isQA) {
        stageColor = '#2ca02c'
        stageName = "QA"
        deployAppDispName = "Deploy QA App"
        collectQAResultDispName = "Collect QA Result"
        undeployAppDispName = "Undeploy QA App"
        collectProcedureName = "CollectQAResult"
        subprocessName = 'Deploy App With Rollback'
    } else {
        stageColor = '#289ce1'
        stageName = "Production"
        deployAppDispName = "Deploy Prod App"
        collectQAResultDispName = "Collect Prod Result"
        undeployAppDispName = "Undeploy Prod App"
        collectProcedureName = "CollectProdResult"
        subprocessName = 'Deploy Application'
    }

    stage stageName, {
        colorCode = stageColor
        pipelineName = pipeName
        gate 'PRE', {
        }

        gate 'POST', {
        }

        task deployAppDispName, {
            environmentName = envName
            environmentProjectName = projName
            rollingDeployEnabled = '0'
            subapplication = envName
            subprocess = subprocessName
            subproject = projName
            taskProcessType = 'APPLICATION'
            taskType = 'PROCESS'
        }

        task collectQAResultDispName, {
            description = ''
            taskType = 'PROCEDURE'
            subproject = projName
            subprocedure = collectProcedureName
            errorHandling = 'stopOnError'
        }

        if(isDev) {
            task 'Testing App', {
                description = ''
                taskType = 'PROCEDURE'
                subproject = projName
                subprocedure = 'TestingApp' 
                errorHandling = 'stopOnError'
            }  
        }

        task 'Check Result', {
            taskType = 'MANUAL'
            approver = [
              'admin',
            ]
        }        

        task undeployAppDispName, {
            environmentName = envName
            environmentProjectName = projName
            rollingDeployEnabled = '0'
            subapplication = envName
            subprocess = 'Uninstall Application'
            subproject = projName
            taskProcessType = 'APPLICATION'
            taskType = 'PROCESS'
        }        
    }
  }
}

def projName  = "$[/myProject/projectName]"
def pipeName = "$[/myProject/projectName]_CD_Release_Pipeline"
def envs = ["dev","qa","prod"]

pipeline pipeName, {
  projectName = projName

  formalParameter 'ec_stagesToRun', {
    expansionDeferred = '1'
  }

  envs.each { env ->

    def envName = (String)"${projName}_${env}"
    def isDev = env.toLowerCase().contains("dev")
    def isQA = env.toLowerCase().contains("qa")
    def isProd = env.toLowerCase().contains("prod")
    def stageColor = ''
    def stageName = ''
    def deployAppDispName = ''
    def collectQAResultDispName = ''
    def undeployAppDispName = ''

    if(isDev) {
        stageColor = '#ff7f0e'
        stageName = "Dev"
        deployAppDispName = "Deploy Dev Apps"
        collectQAResultDispName = "Collect Dev Result"
        undeployAppDispName = "Undeploy Dev App"
        collectProcedureName = "CollectDevResult"
    } else if (isQA) {
        stageColor = '#2ca02c'
        stageName = "QA"
        deployAppDispName = "Deploy QA Apps"
        collectQAResultDispName = "Collect QA Result"
        undeployAppDispName = "Undeploy QA App"
        collectProcedureName = "CollectQAResult"
    } else {
        stageColor = '#289ce1'
        stageName = "Production"
        deployAppDispName = "Deploy Prod Apps"

        collectQAResultDispName = "Collect Prod Result"
        undeployAppDispName = "Undeploy Prod App"
        collectProcedureName = "CollectProdResult"
    }

    stage stageName, {
        colorCode = stageColor
        pipelineName = pipeName
        gate 'PRE', {
        }

        gate 'POST', {
        }

        task deployAppDispName, {
            deployerRunType = 'serial'
            subproject = projName
            taskType = 'DEPLOYER'
        }


        task collectQAResultDispName, {
            description = ''
            taskType = 'PROCEDURE'
            subproject = projName
            subprocedure = collectProcedureName
            errorHandling = 'stopOnError'
        }

        if(isDev) {
            task 'Testing App', {
                description = ''
                taskType = 'PROCEDURE'
                subproject = projName
                subprocedure = 'TestingApp' 
                errorHandling = 'stopOnError'
            }        
        }

        task 'Check Result', {
            taskType = 'MANUAL'
            approver = [
              'admin',
            ]
        }        

		task undeployAppDispName, {
			deployerRunType = 'serial'
			subproject = projName
			taskType = 'DEPLOYER'
		}		
    }
  }
}

