import com.electriccloud.client.groovy.ElectricFlow
import com.electriccloud.client.groovy.models.*
import groovy.json.JsonSlurper 


ElectricFlow ef = new ElectricFlow()
def runtimeid = ''
def pipelineid = ''

def result = ef.getProperty(
                propertyName: '/myPipelineRuntime' )

def runtimeContext = result["property"]["value"].toString()
def matcher = (runtimeContext =~ /id=([\w-]+)/)

if (matcher.find()) {
    runtimeid = matcher.group(1)
    println runtimeid  
} else {
    println "No runtime id found"
    exit 1
}


result = ef.getProperty(
                propertyName: '/myPipeline' )
def pipelineContext = result["property"]["value"].toString()
matcher = (pipelineContext =~ /id=([\w-]+)/)

if (matcher.find()) {
    pipelineid = matcher.group(1)
    println pipelineid   
} else {
    println "No pipeline id found"
    exit 1
}


