//ef.setProperty(path: "/myPipelineStageRuntime/ec_summary/report",  propertyName: "report", value: "https://cdro.idtplateer.com/flow/#pipeline-run/$pipelineid/$runtimeid")

def url = "https://cdro.idtplateer.com/flow/#pipeline-run/$pipelineid/$runtimeid"
def cmd = "ectool setProperty /myPipelineStageRuntime/ec_summary/report --value \"<html><a href=\"${url}\">pipeURL</a></html>\""
def process = Runtime.getRuntime().exec(cmd)
process.waitFor()

def reader = new BufferedReader(new InputStreamReader(process.getInputStream()))
def output = new StringBuffer()

String line
while ((line = reader.readLine()) != null) {
    output.append(line)
    output.append("\n")
}

println(output.toString())
