step.command:
def projName  = "$[/myProject/projectName]"
def envs = ["plateer"]
def resources = []
def environments = []

envs.each { env ->
def envName = (String)"${projName}_${env}"
def ns = (String)"${env}"

environment envName, { 
  projectName = projName

  cluster envName, {
    definitionParameter = [
      'config': '/projects/Hashicup/pluginConfigurations/helmcfg',
      'kubecontext': '',
      'namespace': ns,
    ]
    environmentName = envName
    pluginKey = 'EC-Helm'
  }

  utilityResource 'local', {
    resourceName = 'local'
  }
}

}

def projName = "$[/myProject/projectName]"
def envs = ["plateer"]
def resources = []
def environments = []
def helmValues = ''
def appName = ''
def envName = '' 

envs.each { env ->

    appName = (String)"${projName}_${env}"
    envName = (String)"${env}"

    application appName, {
        applicationType = 'microservice'
        projectName = projName

        microservice appName, {
            applicationName = appName

            definitionSource = 'helm_repository'

            definitionSourceParameter = [
                'repository_credential': '/projects/Hashicup/credentials/harbor_cred',
                'repositoryName': 'hashicup-repo',
                'repositoryUrl': 'https://harbor.idtplateer.com/chartrepo/hashicup',
            ]

            definitionType = 'helm'

            deployParameter = [
                'additionalOptions': '--create-namespace',
                'chart': 'hashicup',
                'releaseName': 'my-hashicup',
                'version': '0.1.0',
                'values': helmValues,
            ]

            rollbackParameter = [
                'rollbackEnabled': 'true',
                'waitTimeout': '300',
            ]
        }

        process 'Deploy Application', {
            description = 'System generated process for microservice application'
            applicationName = appName
            processType = 'DEPLOY'
            formalParameter 'ec_Hashicup-run', defaultValue: '1', {
                expansionDeferred = '1'
                type = 'checkbox'
            }

            property 'ec_deploy', {
                ec_notifierStatus = '0'
            }
        }

        process 'Uninstall Application', {
            applicationName = appName
            exclusiveEnvironment = '0'
            processType = 'UNDEPLOY'
            workspaceName = 'default'

            processStep 'Delete Release', {
                actualParameter = [
                    'actionOnError': '0',
                    'config': '/projects/Hashicup/pluginConfigurations/helmcfg',
                    'errorValue': '',
                    'options': (String)"-n ${envName}",
                    'releaseName': 'my-hashicup',
                    'resultPropertySheet': '/myJob/deleteRelease',
                ]
                dependencyJoinType = 'and'
                processStepType = 'plugin'
                subprocedure = 'Delete Release'
                subproject = '/plugins/EC-Helm/project'

                property 'ec_deploy', {
                    ec_notifierStatus = '0'
                }
            }

            property 'ec_deploy', {
                ec_notifierStatus = '0'
            }
        }


        process 'Junk Process', {
          applicationName = appName
          exclusiveEnvironment = '0'
          processType = 'OTHER'
          workspaceName = 'default'

          processStep 'Junk', {
            actualParameter = [
              'commandToRun': (String)"""
              echo "nothing"
              """,
            ]
            dependencyJoinType = 'and'
            processStepType = 'command'
            subprocedure = 'RunCommand'
            subproject = '/plugins/EC-Core/project'
            useUtilityResource = '1'
            utilityResourceName = 'local'
            property 'ec_deploy', {
              ec_notifierStatus = '0'
            }
          }
          property 'ec_deploy', {
           ec_notifierStatus = '0'
          }
        } 

        property 'ec_deploy', {
            ec_notifierStatus = '0'
        }
    }
}

